contracts/CommunityPools.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

// import "hardhat/console.sol";

/**
 * CommunityPools - Rotating savings and community investment pools
 * Enables digital chamas and collective yield farming
 * @author SafeNest Team
 */
contract CommunityPools {
    // State Variables
    address public immutable owner;
    string public platformName = "Community Pools - Digital Chamas";
    
    // Pool types
    enum PoolType {
        ROTATING_SAVINGS,    // Traditional chama - members take turns receiving funds
        COLLECTIVE_INVESTMENT, // Pool funds for DeFi investments
        EMERGENCY_FUND,      // Community emergency fund
        GOAL_ORIENTED        // Pool for specific community goals
    }
    
    // Pool structure
    struct Pool {
        uint256 id;
        address creator;
        string name;
        string description;
        PoolType poolType;
        address stablecoin;
        uint256 contributionAmount;
        uint256 contributionFrequency; // in days
        uint256 maxMembers;
        uint256 currentMembers;
        uint256 totalContributions;
        uint256 poolBalance;
        bool isActive;
        bool isPublic;
        uint256 createdAt;
        uint256 nextContribution;
        uint256 rotationIndex; // For rotating savings
        mapping(address => bool) members;
        mapping(address => uint256) memberContributions;
        mapping(address => bool) hasReceived; // For rotating savings
    }
    
    // Member structure
    struct Member {
        address user;
        uint256 joinedAt;
        uint256 totalContributed;
        uint256 lastContribution;
        bool isActive;
        uint256 reputation;
    }
    
    // Pool applications
    struct PoolApplication {
        address applicant;
        uint256 poolId;
        string message;
        uint256 appliedAt;
        bool isApproved;
        bool isProcessed;
    }
    
    // Mappings
    mapping(uint256 => Pool) public pools;
    mapping(address => Member[]) public userMemberships;
    mapping(address => uint256[]) public userCreatedPools;
    mapping(uint256 => PoolApplication[]) public poolApplications;
    mapping(address => mapping(uint256 => bool)) public isMember;
    
    // Platform statistics
    uint256 public totalPools = 0;
    uint256 public totalMembers = 0;
    uint256 public totalContributions = 0;
    uint256 public nextPoolId = 1;
    uint256 public platformFeeRate = 20; // 0.2% in basis points
    
    // Events
    event PoolCreated(uint256 indexed poolId, address indexed creator, string name, PoolType poolType);
    event MemberJoined(uint256 indexed poolId, address indexed member);
    event MemberLeft(uint256 indexed poolId, address indexed member);
    event ContributionMade(uint256 indexed poolId, address indexed member, uint256 amount);
    event FundsDistributed(uint256 indexed poolId, address indexed recipient, uint256 amount);
    event ApplicationSubmitted(uint256 indexed poolId, address indexed applicant);
    event ApplicationApproved(uint256 indexed poolId, address indexed applicant);
    event PoolCompleted(uint256 indexed poolId);
    
    // Modifiers
    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }
    
    modifier validPoolId(uint256 _poolId) {
        require(_poolId < nextPoolId, "Invalid pool ID");
        require(pools[_poolId].isActive, "Pool not active");
        _;
    }
    
    modifier onlyPoolMember(uint256 _poolId) {
        require(isMember[msg.sender][_poolId], "Not a pool member");
        _;
    }
    
    modifier onlyPoolCreator(uint256 _poolId) {
        require(pools[_poolId].creator == msg.sender, "Not the pool creator");
        _;
    }
    
    // Constructor
    constructor() {
        owner = msg.sender;
    }
    
    /**
     * Create a new community pool
     */
    function createPool(
        string memory _name,
        string memory _description,
        PoolType _poolType,
        address _stablecoin,
        uint256 _contributionAmount,
        uint256 _contributionFrequency,
        uint256 _maxMembers,
        bool _isPublic
    ) external {
        require(bytes(_name).length > 0, "Pool name required");
        require(_contributionAmount > 0, "Contribution amount must be positive");
        require(_maxMembers > 1, "Pool must have at least 2 members");
        require(_contributionFrequency > 0, "Contribution frequency must be positive");
        
        // Initialize pool struct
        Pool storage newPool = pools[nextPoolId];
        newPool.id = nextPoolId;
        newPool.creator = msg.sender;
        newPool.name = _name;
        newPool.description = _description;
        newPool.poolType = _poolType;
        newPool.stablecoin = _stablecoin;
        newPool.contributionAmount = _contributionAmount;
        newPool.contributionFrequency = _contributionFrequency;
        newPool.maxMembers = _maxMembers;
        newPool.currentMembers = 0;
        newPool.totalContributions = 0;
        newPool.poolBalance = 0;
        newPool.isActive = true;
        newPool.isPublic = _isPublic;
        newPool.createdAt = block.timestamp;
        newPool.nextContribution = block.timestamp + _contributionFrequency * 1 days;
        newPool.rotationIndex = 0;
        
        // Creator automatically becomes first member
        newPool.members[msg.sender] = true;
        newPool.currentMembers = 1;
        isMember[msg.sender][nextPoolId] = true;
        
        userCreatedPools[msg.sender].push(nextPoolId);
        totalPools++;
        totalMembers++;
        
        emit PoolCreated(nextPoolId, msg.sender, _name, _poolType);
        emit MemberJoined(nextPoolId, msg.sender);
    }
    
    /**
     * Join a public pool
     */
    function joinPool(uint256 _poolId) external validPoolId(_poolId) {
        Pool storage pool = pools[_poolId];
        require(pool.isPublic, "Pool is not public");
        require(!isMember[msg.sender][_poolId], "Already a member");
        require(pool.currentMembers < pool.maxMembers, "Pool is full");
        
        pool.members[msg.sender] = true;
        pool.currentMembers++;
        isMember[msg.sender][_poolId] = true;
        totalMembers++;
        
        userMemberships[msg.sender].push(Member({
            user: msg.sender,
            joinedAt: block.timestamp,
            totalContributed: 0,
            lastContribution: 0,
            isActive: true,
            reputation: 0
        }));
        
        emit MemberJoined(_poolId, msg.sender);
    }
    
    /**
     * Apply to join a private pool
     */
    function applyToJoinPool(uint256 _poolId, string memory _message) external validPoolId(_poolId) {
        Pool storage pool = pools[_poolId];
        require(!pool.isPublic, "Pool is public - use joinPool");
        require(!isMember[msg.sender][_poolId], "Already a member");
        require(pool.currentMembers < pool.maxMembers, "Pool is full");
        
        poolApplications[_poolId].push(PoolApplication({
            applicant: msg.sender,
            poolId: _poolId,
            message: _message,
            appliedAt: block.timestamp,
            isApproved: false,
            isProcessed: false
        }));
        
        emit ApplicationSubmitted(_poolId, msg.sender);
    }
    
    /**
     * Approve pool application (only pool creator)
     */
    function approveApplication(uint256 _poolId, uint256 _applicationIndex) external onlyPoolCreator(_poolId) {
        require(_applicationIndex < poolApplications[_poolId].length, "Invalid application index");
        
        PoolApplication storage application = poolApplications[_poolId][_applicationIndex];
        require(!application.isProcessed, "Application already processed");
        
        Pool storage pool = pools[_poolId];
        require(pool.currentMembers < pool.maxMembers, "Pool is full");
        
        application.isApproved = true;
        application.isProcessed = true;
        
        pool.members[application.applicant] = true;
        pool.currentMembers++;
        isMember[application.applicant][_poolId] = true;
        totalMembers++;
        
        userMemberships[application.applicant].push(Member({
            user: application.applicant,
            joinedAt: block.timestamp,
            totalContributed: 0,
            lastContribution: 0,
            isActive: true,
            reputation: 0
        }));
        
        emit ApplicationApproved(_poolId, application.applicant);
        emit MemberJoined(_poolId, application.applicant);
    }
    
    /**
     * Make contribution to pool
     */
    function contributeToPool(uint256 _poolId, uint256 _amount) external onlyPoolMember(_poolId) {
        Pool storage pool = pools[_poolId];
        require(pool.isActive, "Pool not active");
        require(_amount >= pool.contributionAmount, "Contribution below minimum");
        require(block.timestamp >= pool.nextContribution, "Too early for contribution");
        
        // Transfer stablecoin from user
        // Note: In production, use IERC20(pool.stablecoin).transferFrom(msg.sender, address(this), _amount)
        
        pool.memberContributions[msg.sender] += _amount;
        pool.totalContributions += _amount;
        pool.poolBalance += _amount;
        
        // Update member info
        for (uint256 i = 0; i < userMemberships[msg.sender].length; i++) {
            if (userMemberships[msg.sender][i].isActive) {
                userMemberships[msg.sender][i].totalContributed += _amount;
                userMemberships[msg.sender][i].lastContribution = block.timestamp;
                userMemberships[msg.sender][i].reputation += 10; // Reputation points
                break;
            }
        }
        
        // Update next contribution time
        pool.nextContribution = block.timestamp + pool.contributionFrequency * 1 days;
        
        totalContributions += _amount;
        
        emit ContributionMade(_poolId, msg.sender, _amount);
        
        // Handle pool-specific logic
        if (pool.poolType == PoolType.ROTATING_SAVINGS) {
            _handleRotatingSavings(_poolId);
        } else if (pool.poolType == PoolType.COLLECTIVE_INVESTMENT) {
            _handleCollectiveInvestment(_poolId);
        }
    }
    
    /**
     * Handle rotating savings distribution
     */
    function _handleRotatingSavings(uint256 _poolId) internal {
        Pool storage pool = pools[_poolId];
        
        // Check if it's time for distribution
        if (pool.currentMembers > 0 && pool.poolBalance >= pool.contributionAmount * pool.currentMembers) {
            // Find next member to receive funds
            address[] memory members = _getPoolMembers(_poolId);
            address recipient = members[pool.rotationIndex % members.length];
            
            // Ensure member hasn't received funds yet
            while (pool.hasReceived[recipient] && pool.rotationIndex < members.length * 2) {
                pool.rotationIndex++;
                recipient = members[pool.rotationIndex % members.length];
            }
            
            if (!pool.hasReceived[recipient]) {
                uint256 distributionAmount = pool.contributionAmount * pool.currentMembers;
                pool.poolBalance -= distributionAmount;
                pool.hasReceived[recipient] = true;
                pool.rotationIndex++;
                
                // Transfer funds to recipient
                // Note: In production, use IERC20(pool.stablecoin).transfer(recipient, distributionAmount)
                
                emit FundsDistributed(_poolId, recipient, distributionAmount);
                
                // Check if all members have received funds
                bool allReceived = true;
                for (uint256 i = 0; i < members.length; i++) {
                    if (!pool.hasReceived[members[i]]) {
                        allReceived = false;
                        break;
                    }
                }
                
                if (allReceived) {
                    pool.isActive = false;
                    emit PoolCompleted(_poolId);
                }
            }
        }
    }
    
    /**
     * Handle collective investment
     */
    function _handleCollectiveInvestment(uint256 _poolId) internal {
        Pool storage pool = pools[_poolId];
        
        // In production, this would interact with DeFi protocols
        // For now, we just track the balance
    }
    
    /**
     * Withdraw from pool (for non-rotating pools)
     */
    function withdrawFromPool(uint256 _poolId, uint256 _amount) external onlyPoolMember(_poolId) {
        Pool storage pool = pools[_poolId];
        require(pool.poolType != PoolType.ROTATING_SAVINGS, "Cannot withdraw from rotating savings");
        require(pool.poolBalance >= _amount, "Insufficient pool balance");
        require(pool.memberContributions[msg.sender] >= _amount, "Insufficient contribution");
        
        pool.poolBalance -= _amount;
        pool.memberContributions[msg.sender] -= _amount;
        
        // Transfer funds to user
        // Note: In production, use IERC20(pool.stablecoin).transfer(msg.sender, _amount)
        
        emit FundsDistributed(_poolId, msg.sender, _amount);
    }
    
    /**
     * Leave pool
     */
    function leavePool(uint256 _poolId) external onlyPoolMember(_poolId) {
        Pool storage pool = pools[_poolId];
        require(pool.poolType != PoolType.ROTATING_SAVINGS, "Cannot leave rotating savings pool");
        
        pool.members[msg.sender] = false;
        pool.currentMembers--;
        isMember[msg.sender][_poolId] = false;
        totalMembers--;
        
        // Update member status
        for (uint256 i = 0; i < userMemberships[msg.sender].length; i++) {
            if (userMemberships[msg.sender][i].isActive) {
                userMemberships[msg.sender][i].isActive = false;
                break;
            }
        }
        
        emit MemberLeft(_poolId, msg.sender);
    }
    
    /**
     * Get pool members (simplified - in production, maintain a member list)
     */
    function _getPoolMembers(uint256 _poolId) internal view returns (address[] memory) {
        // This is simplified - in production, maintain a proper member list
        address[] memory members = new address[](0);
        return members;
    }
    
    /**
     * Get pool details
     */
    function getPoolDetails(uint256 _poolId) external view validPoolId(_poolId) returns (
        string memory name,
        string memory description,
        PoolType poolType,
        address stablecoin,
        uint256 contributionAmount,
        uint256 contributionFrequency,
        uint256 maxMembers,
        uint256 currentMembers,
        uint256 poolTotalContributions,
        uint256 poolBalance,
        bool isPublic,
        uint256 createdAt
    ) {
        Pool storage pool = pools[_poolId];
        return (
            pool.name,
            pool.description,
            pool.poolType,
            pool.stablecoin,
            pool.contributionAmount,
            pool.contributionFrequency,
            pool.maxMembers,
            pool.currentMembers,
            pool.totalContributions,
            pool.poolBalance,
            pool.isPublic,
            pool.createdAt
        );
    }
    
    /**
     * Get user's pool memberships
     */
    function getUserMemberships(address _user) external view returns (uint256[] memory) {
        uint256[] memory memberships = new uint256[](userMemberships[_user].length);
        uint256 count = 0;
        
        for (uint256 i = 0; i < userMemberships[_user].length; i++) {
            if (userMemberships[_user][i].isActive) {
                memberships[count] = i; // Pool ID would be stored differently in production
                count++;
            }
        }
        
        return memberships;
    }
    
    /**
     * Get platform statistics
     */
    function getPlatformStats() external view returns (
        uint256 _totalPools,
        uint256 _totalMembers,
        uint256 _totalContributionsAmount,
        uint256 _platformFeeRate
    ) {
        return (totalPools, totalMembers, totalContributions, platformFeeRate);
    }
    
    /**
     * Set platform fee rate (only owner)
     */
    function setPlatformFeeRate(uint256 _newRate) external onlyOwner {
        require(_newRate <= 1000, "Fee rate too high"); // Max 10%
        platformFeeRate = _newRate;
    }
}

contracts/CommunityPoolsAdvanced.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

/**
 * CommunityPoolsAdvanced - Advanced community pools with digital chamas
 * Implements rotating savings, investment pools, and goal-oriented savings
 * @author SafeNest Team
 */
contract CommunityPoolsAdvanced {
    // State Variables
    address public immutable owner;
    string public platformName = "Community Pools Advanced - Digital Chamas";
    
    // Pool types
    enum PoolType {
        ROTATING_SAVINGS,    // Traditional chama - members take turns receiving funds
        COLLECTIVE_INVESTMENT, // Pool funds for DeFi investments
        EMERGENCY_FUND,      // Community emergency fund
        GOAL_ORIENTED        // Pool for specific community goals
    }
    
    // Pool structure
    struct Pool {
        uint256 id;
        address creator;
        string name;
        string description;
        PoolType poolType;
        uint256 contributionAmount;
        uint256 contributionFrequency; // in days
        uint256 maxMembers;
        uint256 currentMembers;
        uint256 totalContributions;
        uint256 poolBalance;
        uint256 yieldRate;
        bool isActive;
        bool isPublic;
        uint256 createdAt;
        uint256 nextContribution;
        uint256 rotationIndex; // For rotating savings
        uint256 lastDistribution;
        mapping(address => bool) members;
        mapping(address => uint256) memberContributions;
        mapping(address => bool) hasReceived; // For rotating savings
        mapping(address => uint256) memberShares;
    }
    
    // Member structure
    struct Member {
        address user;
        uint256 joinedAt;
        uint256 totalContributed;
        uint256 lastContribution;
        bool isActive;
        uint256 reputation;
        uint256 shares;
    }
    
    // Pool applications
    struct PoolApplication {
        address applicant;
        uint256 poolId;
        string message;
        uint256 appliedAt;
        bool isApproved;
        bool isProcessed;
    }
    
    // Pool statistics
    struct PoolStats {
        uint256 totalPools;
        uint256 totalMembers;
        uint256 totalContributions;
        uint256 totalDistributions;
        uint256 averageYield;
    }
    
    // Mappings
    mapping(uint256 => Pool) public pools;
    mapping(address => Member[]) public userMemberships;
    mapping(address => uint256[]) public userCreatedPools;
    mapping(uint256 => PoolApplication[]) public poolApplications;
    mapping(address => mapping(uint256 => bool)) public isMember;
    mapping(address => uint256) public userReputation;
    
    // Platform statistics
    PoolStats public platformStats;
    uint256 public nextPoolId = 1;
    uint256 public platformFeeRate = 20; // 0.2% in basis points
    uint256 public reputationThreshold = 100; // Minimum reputation to create pools
    
    // Yield distribution
    uint256 public totalYieldDistributed = 0;
    uint256 public lastYieldDistribution = 0;
    uint256 public yieldDistributionInterval = 7 days;
    
    // Events
    event PoolCreated(uint256 indexed poolId, address indexed creator, string name, PoolType poolType);
    event MemberJoined(uint256 indexed poolId, address indexed member);
    event MemberLeft(uint256 indexed poolId, address indexed member);
    event ContributionMade(uint256 indexed poolId, address indexed member, uint256 amount);
    event FundsDistributed(uint256 indexed poolId, address indexed recipient, uint256 amount);
    event ApplicationSubmitted(uint256 indexed poolId, address indexed applicant);
    event ApplicationApproved(uint256 indexed poolId, address indexed applicant);
    event PoolCompleted(uint256 indexed poolId);
    event ReputationUpdated(address indexed user, uint256 oldReputation, uint256 newReputation);
    event YieldDistributed(uint256 indexed poolId, uint256 amount, uint256 timestamp);
    event PoolTypeChanged(uint256 indexed poolId, PoolType oldType, PoolType newType);
    
    // Modifiers
    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }
    
    modifier validPoolId(uint256 _poolId) {
        require(_poolId < nextPoolId, "Invalid pool ID");
        require(pools[_poolId].isActive, "Pool not active");
        _;
    }
    
    modifier onlyPoolMember(uint256 _poolId) {
        require(isMember[msg.sender][_poolId], "Not a pool member");
        _;
    }
    
    modifier onlyPoolCreator(uint256 _poolId) {
        require(pools[_poolId].creator == msg.sender, "Not the pool creator");
        _;
    }
    
    modifier validAmount(uint256 _amount) {
        require(_amount > 0, "Amount must be greater than 0");
        _;
    }
    
    // Constructor
    constructor() {
        owner = msg.sender;
    }
    
    /**
     * Create a new community pool
     */
    function createPool(
        string memory _name,
        string memory _description,
        PoolType _poolType,
        uint256 _contributionAmount,
        uint256 _contributionFrequency,
        uint256 _maxMembers,
        bool _isPublic
    ) external {
        require(bytes(_name).length > 0, "Pool name required");
        require(_contributionAmount > 0, "Contribution amount must be positive");
        require(_maxMembers > 1, "Pool must have at least 2 members");
        require(_contributionFrequency > 0, "Contribution frequency must be positive");
        require(userReputation[msg.sender] >= reputationThreshold, "Insufficient reputation");
        
        // Initialize pool struct
        Pool storage newPool = pools[nextPoolId];
        newPool.id = nextPoolId;
        newPool.creator = msg.sender;
        newPool.name = _name;
        newPool.description = _description;
        newPool.poolType = _poolType;
        newPool.contributionAmount = _contributionAmount;
        newPool.contributionFrequency = _contributionFrequency;
        newPool.maxMembers = _maxMembers;
        newPool.currentMembers = 0;
        newPool.totalContributions = 0;
        newPool.poolBalance = 0;
        newPool.yieldRate = 0;
        newPool.isActive = true;
        newPool.isPublic = _isPublic;
        newPool.createdAt = block.timestamp;
        newPool.nextContribution = block.timestamp + _contributionFrequency * 1 days;
        newPool.rotationIndex = 0;
        newPool.lastDistribution = 0;
        
        // Creator automatically becomes first member
        newPool.members[msg.sender] = true;
        newPool.currentMembers = 1;
        isMember[msg.sender][nextPoolId] = true;
        
        userCreatedPools[msg.sender].push(nextPoolId);
        platformStats.totalPools++;
        platformStats.totalMembers++;
        
        emit PoolCreated(nextPoolId, msg.sender, _name, _poolType);
        emit MemberJoined(nextPoolId, msg.sender);
        nextPoolId++;
    }
    
    /**
     * Join a public pool
     */
    function joinPool(uint256 _poolId) external validPoolId(_poolId) {
        Pool storage pool = pools[_poolId];
        require(pool.isPublic, "Pool is not public");
        require(!isMember[msg.sender][_poolId], "Already a member");
        require(pool.currentMembers < pool.maxMembers, "Pool is full");
        
        pool.members[msg.sender] = true;
        pool.currentMembers++;
        isMember[msg.sender][_poolId] = true;
        platformStats.totalMembers++;
        
        userMemberships[msg.sender].push(Member({
            user: msg.sender,
            joinedAt: block.timestamp,
            totalContributed: 0,
            lastContribution: 0,
            isActive: true,
            reputation: 0,
            shares: 0
        }));
        
        emit MemberJoined(_poolId, msg.sender);
    }
    
    /**
     * Apply to join a private pool
     */
    function applyToJoinPool(uint256 _poolId, string memory _message) external validPoolId(_poolId) {
        Pool storage pool = pools[_poolId];
        require(!pool.isPublic, "Pool is public - use joinPool");
        require(!isMember[msg.sender][_poolId], "Already a member");
        require(pool.currentMembers < pool.maxMembers, "Pool is full");
        
        poolApplications[_poolId].push(PoolApplication({
            applicant: msg.sender,
            poolId: _poolId,
            message: _message,
            appliedAt: block.timestamp,
            isApproved: false,
            isProcessed: false
        }));
        
        emit ApplicationSubmitted(_poolId, msg.sender);
    }
    
    /**
     * Approve pool application (only pool creator)
     */
    function approveApplication(uint256 _poolId, uint256 _applicationIndex) external onlyPoolCreator(_poolId) {
        require(_applicationIndex < poolApplications[_poolId].length, "Invalid application index");
        
        PoolApplication storage application = poolApplications[_poolId][_applicationIndex];
        require(!application.isProcessed, "Application already processed");
        
        Pool storage pool = pools[_poolId];
        require(pool.currentMembers < pool.maxMembers, "Pool is full");
        
        application.isApproved = true;
        application.isProcessed = true;
        
        pool.members[application.applicant] = true;
        pool.currentMembers++;
        isMember[application.applicant][_poolId] = true;
        platformStats.totalMembers++;
        
        userMemberships[application.applicant].push(Member({
            user: application.applicant,
            joinedAt: block.timestamp,
            totalContributed: 0,
            lastContribution: 0,
            isActive: true,
            reputation: 0,
            shares: 0
        }));
        
        emit ApplicationApproved(_poolId, application.applicant);
        emit MemberJoined(_poolId, application.applicant);
    }
    
    /**
     * Make contribution to pool
     */
    function contributeToPool(uint256 _poolId, uint256 _amount) external onlyPoolMember(_poolId) validAmount(_amount) payable {
        Pool storage pool = pools[_poolId];
        require(pool.isActive, "Pool not active");
        require(_amount >= pool.contributionAmount, "Contribution below minimum");
        require(block.timestamp >= pool.nextContribution, "Too early for contribution");
        require(msg.value >= _amount, "Insufficient ETH sent");
        
        pool.memberContributions[msg.sender] += _amount;
        pool.totalContributions += _amount;
        pool.poolBalance += _amount;
        
        // Update member info
        for (uint256 i = 0; i < userMemberships[msg.sender].length; i++) {
            if (userMemberships[msg.sender][i].isActive) {
                userMemberships[msg.sender][i].totalContributed += _amount;
                userMemberships[msg.sender][i].lastContribution = block.timestamp;
                userMemberships[msg.sender][i].reputation += 10; // Reputation points
                userMemberships[msg.sender][i].shares += _amount;
                break;
            }
        }
        
        // Update user reputation
        uint256 oldReputation = userReputation[msg.sender];
        userReputation[msg.sender] += 10;
        emit ReputationUpdated(msg.sender, oldReputation, userReputation[msg.sender]);
        
        // Update next contribution time
        pool.nextContribution = block.timestamp + pool.contributionFrequency * 1 days;
        
        platformStats.totalContributions += _amount;
        
        emit ContributionMade(_poolId, msg.sender, _amount);
        
        // Handle pool-specific logic
        if (pool.poolType == PoolType.ROTATING_SAVINGS) {
            _handleRotatingSavings(_poolId);
        } else if (pool.poolType == PoolType.COLLECTIVE_INVESTMENT) {
            _handleCollectiveInvestment(_poolId);
        }
    }
    
    /**
     * Handle rotating savings distribution
     */
    function _handleRotatingSavings(uint256 _poolId) internal {
        Pool storage pool = pools[_poolId];
        
        // Check if it's time for distribution
        if (pool.currentMembers > 0 && pool.poolBalance >= pool.contributionAmount * pool.currentMembers) {
            // Find next member to receive funds
            address[] memory members = _getPoolMembers(_poolId);
            address recipient = members[pool.rotationIndex % members.length];
            
            // Ensure member hasn't received funds yet
            while (pool.hasReceived[recipient] && pool.rotationIndex < members.length * 2) {
                pool.rotationIndex++;
                recipient = members[pool.rotationIndex % members.length];
            }
            
            if (!pool.hasReceived[recipient]) {
                uint256 distributionAmount = pool.contributionAmount * pool.currentMembers;
                pool.poolBalance -= distributionAmount;
                pool.hasReceived[recipient] = true;
                pool.rotationIndex++;
                
                // Transfer funds to recipient
                (bool success, ) = recipient.call{value: distributionAmount}("");
                require(success, "Transfer failed");
                
                platformStats.totalDistributions += distributionAmount;
                
                emit FundsDistributed(_poolId, recipient, distributionAmount);
                
                // Check if all members have received funds
                bool allReceived = true;
                for (uint256 i = 0; i < members.length; i++) {
                    if (!pool.hasReceived[members[i]]) {
                        allReceived = false;
                        break;
                    }
                }
                
                if (allReceived) {
                    pool.isActive = false;
                    emit PoolCompleted(_poolId);
                }
            }
        }
    }
    
    /**
     * Handle collective investment
     */
    function _handleCollectiveInvestment(uint256 _poolId) internal {
        Pool storage pool = pools[_poolId];
        
        // In production, this would interact with DeFi protocols
        // For now, we just track the balance and calculate yield
        if (pool.poolBalance > 0) {
            // Simulate yield calculation (in production, use real DeFi yields)
            uint256 yieldAmount = (pool.poolBalance * 500) / 10000; // 5% APY
            pool.poolBalance += yieldAmount;
            pool.yieldRate = 500; // 5% in basis points
            
            emit YieldDistributed(_poolId, yieldAmount, block.timestamp);
        }
    }
    
    /**
     * Withdraw from pool (for non-rotating pools)
     */
    function withdrawFromPool(uint256 _poolId, uint256 _amount) external onlyPoolMember(_poolId) validAmount(_amount) {
        Pool storage pool = pools[_poolId];
        require(pool.poolType != PoolType.ROTATING_SAVINGS, "Cannot withdraw from rotating savings");
        require(pool.poolBalance >= _amount, "Insufficient pool balance");
        require(pool.memberContributions[msg.sender] >= _amount, "Insufficient contribution");
        
        pool.poolBalance -= _amount;
        pool.memberContributions[msg.sender] -= _amount;
        
        // Transfer funds to user
        (bool success, ) = msg.sender.call{value: _amount}("");
        require(success, "Transfer failed");
        
        emit FundsDistributed(_poolId, msg.sender, _amount);
    }
    
    /**
     * Leave pool
     */
    function leavePool(uint256 _poolId) external onlyPoolMember(_poolId) {
        Pool storage pool = pools[_poolId];
        require(pool.poolType != PoolType.ROTATING_SAVINGS, "Cannot leave rotating savings pool");
        
        pool.members[msg.sender] = false;
        pool.currentMembers--;
        isMember[msg.sender][_poolId] = false;
        platformStats.totalMembers--;
        
        // Update member status
        for (uint256 i = 0; i < userMemberships[msg.sender].length; i++) {
            if (userMemberships[msg.sender][i].isActive) {
                userMemberships[msg.sender][i].isActive = false;
                break;
            }
        }
        
        emit MemberLeft(_poolId, msg.sender);
    }
    
    /**
     * Get pool members (simplified - in production, maintain a member list)
     */
    function _getPoolMembers(uint256 _poolId) internal view returns (address[] memory) {
        // This is simplified - in production, maintain a proper member list
        address[] memory members = new address[](0);
        return members;
    }
    
    /**
     * Get pool details
     */
    function getPoolDetails(uint256 _poolId) external view validPoolId(_poolId) returns (
        string memory name,
        string memory description,
        PoolType poolType,
        uint256 contributionAmount,
        uint256 contributionFrequency,
        uint256 maxMembers,
        uint256 currentMembers,
        uint256 totalContributions,
        uint256 poolBalance,
        uint256 yieldRate,
        bool isPublic,
        uint256 createdAt
    ) {
        Pool storage pool = pools[_poolId];
        return (
            pool.name,
            pool.description,
            pool.poolType,
            pool.contributionAmount,
            pool.contributionFrequency,
            pool.maxMembers,
            pool.currentMembers,
            pool.totalContributions,
            pool.poolBalance,
            pool.yieldRate,
            pool.isPublic,
            pool.createdAt
        );
    }
    
    /**
     * Get user's pool memberships
     */
    function getUserMemberships(address _user) external view returns (uint256[] memory) {
        uint256[] memory memberships = new uint256[](userMemberships[_user].length);
        uint256 count = 0;
        
        for (uint256 i = 0; i < userMemberships[_user].length; i++) {
            if (userMemberships[_user][i].isActive) {
                memberships[count] = i; // Pool ID would be stored differently in production
                count++;
            }
        }
        
        return memberships;
    }
    
    /**
     * Get platform statistics
     */
    function getPlatformStats() external view returns (
        uint256 _totalPools,
        uint256 _totalMembers,
        uint256 _totalContributions,
        uint256 _totalDistributions,
        uint256 _averageYield,
        uint256 _platformFeeRate
    ) {
        return (
            platformStats.totalPools,
            platformStats.totalMembers,
            platformStats.totalContributions,
            platformStats.totalDistributions,
            platformStats.averageYield,
            platformFeeRate
        );
    }
    
    /**
     * Get user reputation
     */
    function getUserReputation(address _user) external view returns (uint256) {
        return userReputation[_user];
    }
    
    /**
     * Set platform fee rate (only owner)
     */
    function setPlatformFeeRate(uint256 _newRate) external onlyOwner {
        require(_newRate <= 1000, "Fee rate too high"); // Max 10%
        platformFeeRate = _newRate;
    }
    
    /**
     * Set reputation threshold (only owner)
     */
    function setReputationThreshold(uint256 _newThreshold) external onlyOwner {
        reputationThreshold = _newThreshold;
    }
    
    /**
     * Withdraw platform fees (only owner)
     */
    function withdrawFees() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No fees to withdraw");
        
        (bool success, ) = owner.call{value: balance}("");
        require(success, "Failed to withdraw fees");
    }
    
    receive() external payable {}
}

contracts/INCLToken.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

// import "hardhat/console.sol";

/**
 * INCL Token - Governance token for MicroSavings platform
 * Enables community governance and fee sharing
 * @author SafeNest Team
 */
contract INCLToken {
    // Token details
    string public name = "Inclusive Finance Token";
    string public symbol = "INCL";
    uint8 public decimals = 18;
    uint256 public totalSupply;
    
    // Owner and platform
    address public immutable owner;
    address public platformContract;
    
    // Balances and allowances
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    
    // Staking for governance
    struct Stake {
        uint256 amount;
        uint256 timestamp;
        uint256 lockPeriod;
        bool isActive;
    }
    
    mapping(address => Stake[]) public userStakes;
    mapping(address => uint256) public totalStaked;
    
    // Governance
    struct Proposal {
        uint256 id;
        address proposer;
        string title;
        string description;
        uint256 forVotes;
        uint256 againstVotes;
        uint256 startTime;
        uint256 endTime;
        bool executed;
        bool isActive;
    }
    
    mapping(uint256 => Proposal) public proposals;
    mapping(address => mapping(uint256 => bool)) public hasVoted;
    uint256 public nextProposalId = 1;
    uint256 public votingPowerThreshold = 1000 * 10**18; // 1000 INCL tokens
    uint256 public proposalDuration = 3 days;
    
    // Fee sharing
    uint256 public totalFeesCollected = 0;
    mapping(address => uint256) public userFeeShare;
    uint256 public lastFeeDistribution = 0;
    uint256 public feeDistributionInterval = 7 days;
    
    // Events
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
    event TokensMinted(address indexed to, uint256 amount);
    event TokensBurned(address indexed from, uint256 amount);
    event Staked(address indexed user, uint256 amount, uint256 lockPeriod);
    event Unstaked(address indexed user, uint256 amount);
    event ProposalCreated(uint256 indexed proposalId, address indexed proposer, string title);
    event VoteCast(address indexed voter, uint256 indexed proposalId, bool support, uint256 votes);
    event ProposalExecuted(uint256 indexed proposalId);
    event FeeDistributed(address indexed user, uint256 amount);
    
    // Modifiers
    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }
    
    modifier onlyPlatform() {
        require(msg.sender == platformContract, "Not the platform contract");
        _;
    }
    
    modifier validAmount(uint256 _amount) {
        require(_amount > 0, "Amount must be greater than 0");
        _;
    }
    
    // Constructor
    constructor() {
        owner = msg.sender;
        totalSupply = 1000000000 * 10**18; // 1 billion tokens
        balanceOf[msg.sender] = totalSupply;
        emit Transfer(address(0), msg.sender, totalSupply);
    }
    
    /**
     * Set platform contract address
     */
    function setPlatformContract(address _platform) external onlyOwner {
        platformContract = _platform;
    }
    
    /**
     * Transfer tokens
     */
    function transfer(address _to, uint256 _value) external returns (bool) {
        require(balanceOf[msg.sender] >= _value, "Insufficient balance");
        require(_to != address(0), "Invalid recipient");
        
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
        
        emit Transfer(msg.sender, _to, _value);
        return true;
    }
    
    /**
     * Approve spender
     */
    function approve(address _spender, uint256 _value) external returns (bool) {
        allowance[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }
    
    /**
     * Transfer from (for allowances)
     */
    function transferFrom(address _from, address _to, uint256 _value) external returns (bool) {
        require(balanceOf[_from] >= _value, "Insufficient balance");
        require(allowance[_from][msg.sender] >= _value, "Insufficient allowance");
        require(_to != address(0), "Invalid recipient");
        
        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        allowance[_from][msg.sender] -= _value;
        
        emit Transfer(_from, _to, _value);
        return true;
    }
    
    /**
     * Mint tokens (only platform can mint as rewards)
     */
    function mint(address _to, uint256 _amount) external onlyPlatform validAmount(_amount) {
        totalSupply += _amount;
        balanceOf[_to] += _amount;
        emit TokensMinted(_to, _amount);
        emit Transfer(address(0), _to, _amount);
    }
    
    /**
     * Burn tokens
     */
    function burn(uint256 _amount) external validAmount(_amount) {
        require(balanceOf[msg.sender] >= _amount, "Insufficient balance");
        
        balanceOf[msg.sender] -= _amount;
        totalSupply -= _amount;
        emit TokensBurned(msg.sender, _amount);
        emit Transfer(msg.sender, address(0), _amount);
    }
    
    /**
     * Stake tokens for governance power
     */
    function stake(uint256 _amount, uint256 _lockPeriod) external validAmount(_amount) {
        require(balanceOf[msg.sender] >= _amount, "Insufficient balance");
        require(_lockPeriod >= 30 days, "Minimum lock period is 30 days");
        require(_lockPeriod <= 365 days, "Maximum lock period is 365 days");
        
        balanceOf[msg.sender] -= _amount;
        totalStaked[msg.sender] += _amount;
        
        userStakes[msg.sender].push(Stake({
            amount: _amount,
            timestamp: block.timestamp,
            lockPeriod: _lockPeriod,
            isActive: true
        }));
        
        emit Staked(msg.sender, _amount, _lockPeriod);
    }
    
    /**
     * Unstake tokens (after lock period)
     */
    function unstake(uint256 _stakeIndex) external {
        require(_stakeIndex < userStakes[msg.sender].length, "Invalid stake index");
        
        Stake storage userStake = userStakes[msg.sender][_stakeIndex];
        require(userStake.isActive, "Stake not active");
        require(
            block.timestamp >= userStake.timestamp + userStake.lockPeriod,
            "Stake still locked"
        );
        
        userStake.isActive = false;
        totalStaked[msg.sender] -= userStake.amount;
        balanceOf[msg.sender] += userStake.amount;
        
        emit Unstaked(msg.sender, userStake.amount);
    }
    
    /**
     * Get user's voting power
     */
    function getVotingPower(address _user) external view returns (uint256) {
        uint256 power = 0;
        
        for (uint256 i = 0; i < userStakes[msg.sender].length; i++) {
            if (userStakes[msg.sender][i].isActive) {
                // Longer lock periods give more voting power
                uint256 multiplier = 100 + (userStakes[msg.sender][i].lockPeriod / 30 days) * 10;
                power += userStakes[msg.sender][i].amount * multiplier / 100;
            }
        }
        
        return power;
    }
    
    /**
     * Create governance proposal
     */
    function createProposal(
        string memory _title,
        string memory _description
    ) external {
        require(totalStaked[msg.sender] >= votingPowerThreshold, "Insufficient voting power");
        
        proposals[nextProposalId] = Proposal({
            id: nextProposalId,
            proposer: msg.sender,
            title: _title,
            description: _description,
            forVotes: 0,
            againstVotes: 0,
            startTime: block.timestamp,
            endTime: block.timestamp + proposalDuration,
            executed: false,
            isActive: true
        });
        
        emit ProposalCreated(nextProposalId, msg.sender, _title);
        nextProposalId++;
    }
    
    /**
     * Vote on proposal
     */
    function vote(uint256 _proposalId, bool _support) external {
        Proposal storage proposal = proposals[_proposalId];
        require(proposal.isActive, "Proposal not active");
        require(block.timestamp <= proposal.endTime, "Voting period ended");
        require(!hasVoted[msg.sender][_proposalId], "Already voted");
        
        uint256 votingPower = this.getVotingPower(msg.sender);
        require(votingPower > 0, "No voting power");
        
        hasVoted[msg.sender][_proposalId] = true;
        
        if (_support) {
            proposal.forVotes += votingPower;
        } else {
            proposal.againstVotes += votingPower;
        }
        
        emit VoteCast(msg.sender, _proposalId, _support, votingPower);
    }
    
    /**
     * Execute proposal (if passed)
     */
    function executeProposal(uint256 _proposalId) external {
        Proposal storage proposal = proposals[_proposalId];
        require(proposal.isActive, "Proposal not active");
        require(block.timestamp > proposal.endTime, "Voting period not ended");
        require(!proposal.executed, "Proposal already executed");
        require(proposal.forVotes > proposal.againstVotes, "Proposal not passed");
        
        proposal.executed = true;
        proposal.isActive = false;
        
        emit ProposalExecuted(_proposalId);
    }
    
    /**
     * Collect platform fees (only platform contract)
     */
    function collectFees(uint256 _amount) external onlyPlatform {
        totalFeesCollected += _amount;
    }
    
    /**
     * Distribute fees to token holders (simplified)
     */
    function distributeFees() external {
        require(
            block.timestamp >= lastFeeDistribution + feeDistributionInterval,
            "Distribution interval not met"
        );
        
        // Simplified fee distribution - in production, implement proper staker iteration
        lastFeeDistribution = block.timestamp;
        totalFeesCollected = 0;
    }
    
    /**
     * Claim fee rewards
     */
    function claimFeeRewards() external {
        uint256 rewards = userFeeShare[msg.sender];
        require(rewards > 0, "No rewards to claim");
        
        userFeeShare[msg.sender] = 0;
        balanceOf[msg.sender] += rewards;
        
        emit Transfer(address(0), msg.sender, rewards);
    }
    
    /**
     * Get proposal details
     */
    function getProposal(uint256 _proposalId) external view returns (
        address proposer,
        string memory title,
        string memory description,
        uint256 forVotes,
        uint256 againstVotes,
        uint256 startTime,
        uint256 endTime,
        bool executed,
        bool isActive
    ) {
        Proposal memory proposal = proposals[_proposalId];
        return (
            proposal.proposer,
            proposal.title,
            proposal.description,
            proposal.forVotes,
            proposal.againstVotes,
            proposal.startTime,
            proposal.endTime,
            proposal.executed,
            proposal.isActive
        );
    }
    
    /**
     * Get user's staking info
     */
    function getUserStakes(address _user) external view returns (
        uint256 totalStakedAmount,
        uint256 activeStakes,
        uint256 totalVotingPower
    ) {
        totalStakedAmount = totalStaked[_user];
        activeStakes = 0;
        totalVotingPower = 0;
        
        for (uint256 i = 0; i < userStakes[_user].length; i++) {
            if (userStakes[_user][i].isActive) {
                activeStakes++;
                uint256 multiplier = 100 + (userStakes[_user][i].lockPeriod / 30 days) * 10;
                totalVotingPower += userStakes[_user][i].amount * multiplier / 100;
            }
        }
    }
}

contracts/InclusiveFinanceFlow.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

/**
 * InclusiveFinanceFlow - Complete smart contract flow for deposit  yield  pension vault
 * Implements the full user journey from micro-savings to retirement planning
 * @author SafeNest Team
 */
contract InclusiveFinanceFlow {
    // State Variables
    address public immutable owner;
    string public platformName = "Inclusive Finance - Complete DeFi Flow";
    
    // User Account Structure
    struct UserAccount {
        address user;
        uint256 totalDeposited;
        uint256 totalWithdrawn;
        uint256 currentBalance;
        uint256 emergencyFund;
        uint256 pensionFund;
        uint256 communityPoolShares;
        uint256 lastDepositTime;
        uint256 lastWithdrawalTime;
        bool isActive;
        bool hasInsurance;
        bool isPremiumUser;
        uint256 referralCount;
        uint256 totalReferralRewards;
        uint256 educationScore;
        uint256 riskProfile; // 1=Conservative, 2=Moderate, 3=Aggressive
    }
    
    // Pension Vault Structure
    struct PensionVault {
        address user;
        uint256 totalDeposited;
        uint256 currentValue;
        uint256 retirementAge;
        uint256 vestingStartTime;
        uint256 vestingEndTime;
        bool isVesting;
        bool isWithdrawn;
        uint256 monthlyWithdrawalAmount;
        uint256 lastWithdrawalTime;
    }
    
    // Emergency Fund Structure
    struct EmergencyFund {
        address user;
        uint256 balance;
        uint256 lastDepositTime;
        uint256 withdrawalCount;
        uint256 totalWithdrawn;
        bool isActive;
        uint256 incentiveEarned;
    }
    
    // Community Pool Structure
    struct CommunityPool {
        uint256 id;
        address creator;
        string name;
        uint256 totalDeposits;
        uint256 memberCount;
        uint256 yieldRate;
        bool isActive;
        uint256 createdAt;
        mapping(address => uint256) memberShares;
        mapping(address => bool) isMember;
    }
    
    // Yield Strategy Structure
    struct YieldStrategy {
        uint256 id;
        string name;
        address strategyContract;
        uint256 apy;
        uint256 riskLevel; // 1=Low, 2=Medium, 3=High
        bool isActive;
        uint256 minDeposit;
        uint256 maxDeposit;
        uint256 totalDeposited;
        uint256 currentYield;
    }
    
    // Insurance Policy Structure
    struct InsurancePolicy {
        address user;
        uint256 coverageAmount;
        uint256 premiumRate;
        uint256 coverageStartTime;
        uint256 coverageEndTime;
        bool isActive;
        uint256 claimsCount;
        uint256 totalClaimsPaid;
    }
    
    // Referral Structure
    struct ReferralData {
        address referrer;
        address referee;
        uint256 depositAmount;
        uint256 rewardAmount;
        uint256 timestamp;
        bool isPaid;
    }
    
    // Mappings
    mapping(address => UserAccount) public userAccounts;
    mapping(address => PensionVault) public pensionVaults;
    mapping(address => EmergencyFund) public emergencyFunds;
    mapping(uint256 => CommunityPool) public communityPools;
    mapping(uint256 => YieldStrategy) public yieldStrategies;
    mapping(address => InsurancePolicy) public insurancePolicies;
    mapping(address => address) public referrers; // referee => referrer
    mapping(address => ReferralData[]) public referralHistory;
    
    // Platform Statistics
    uint256 public totalUsers = 0;
    uint256 public totalDeposited = 0;
    uint256 public totalWithdrawn = 0;
    uint256 public totalEmergencyFunds = 0;
    uint256 public totalPensionFunds = 0;
    uint256 public totalCommunityPools = 0;
    uint256 public totalInsurancePolicies = 0;
    uint256 public totalReferralRewards = 0;
    
    // Platform Parameters
    uint256 public platformFeeRate = 30; // 0.3%
    uint256 public emergencyWithdrawalFee = 50; // 0.5%
    uint256 public pensionYieldBoost = 200; // 2%
    uint256 public referralBonusRate = 100; // 1%
    uint256 public insurancePremiumRate = 10; // 0.1%
    uint256 public communityPoolYieldBoost = 100; // 1%
    
    // Counters
    uint256 public nextCommunityPoolId = 1;
    uint256 public nextYieldStrategyId = 1;
    
    // Events
    event UserRegistered(address indexed user, address indexed referrer, uint256 timestamp);
    event DepositMade(address indexed user, uint256 amount, uint256 emergencyAmount, uint256 pensionAmount, uint256 timestamp);
    event WithdrawalMade(address indexed user, uint256 amount, uint256 fee, uint256 timestamp);
    event EmergencyWithdrawal(address indexed user, uint256 amount, uint256 fee, uint256 timestamp);
    event PensionDeposit(address indexed user, uint256 amount, uint256 retirementAge, uint256 timestamp);
    event PensionWithdrawal(address indexed user, uint256 amount, uint256 timestamp);
    event CommunityPoolJoined(address indexed user, uint256 poolId, uint256 amount, uint256 timestamp);
    event CommunityPoolCreated(address indexed creator, uint256 poolId, string name, uint256 timestamp);
    event YieldStrategyAdded(uint256 indexed strategyId, string name, uint256 apy, uint256 riskLevel, uint256 timestamp);
    event InsurancePolicyPurchased(address indexed user, uint256 coverageAmount, uint256 premiumRate, uint256 timestamp);
    event InsuranceClaimProcessed(address indexed user, uint256 claimAmount, uint256 timestamp);
    event ReferralRewardPaid(address indexed referrer, address indexed referee, uint256 amount, uint256 timestamp);
    event PremiumFeatureUnlocked(address indexed user, string feature, uint256 timestamp);
    event RiskProfileUpdated(address indexed user, uint256 oldProfile, uint256 newProfile, uint256 timestamp);
    event EducationScoreUpdated(address indexed user, uint256 oldScore, uint256 newScore, uint256 timestamp);
    
    // Modifiers
    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }
    
    modifier validUser(address _user) {
        require(userAccounts[_user].isActive, "User not registered");
        _;
    }
    
    modifier validAmount(uint256 _amount) {
        require(_amount > 0, "Amount must be greater than 0");
        _;
    }
    
    // Constructor
    constructor() {
        owner = msg.sender;
    }
    
    /**
     * Register a new user with optional referrer
     */
    function registerUser(address _referrer) external {
        require(!userAccounts[msg.sender].isActive, "User already registered");
        
        userAccounts[msg.sender] = UserAccount({
            user: msg.sender,
            totalDeposited: 0,
            totalWithdrawn: 0,
            currentBalance: 0,
            emergencyFund: 0,
            pensionFund: 0,
            communityPoolShares: 0,
            lastDepositTime: 0,
            lastWithdrawalTime: 0,
            isActive: true,
            hasInsurance: false,
            isPremiumUser: false,
            referralCount: 0,
            totalReferralRewards: 0,
            educationScore: 0,
            riskProfile: 1 // Default to conservative
        });
        
        // Set referrer if provided and valid
        if (_referrer != address(0) && _referrer != msg.sender && userAccounts[_referrer].isActive) {
            referrers[msg.sender] = _referrer;
            userAccounts[_referrer].referralCount++;
        }
        
        totalUsers++;
        
        emit UserRegistered(msg.sender, _referrer, block.timestamp);
    }
    
    /**
     * Make a deposit with automatic allocation to emergency fund and pension
     */
    function makeDeposit(
        uint256 _emergencyPercentage, // Percentage for emergency fund (0-100)
        uint256 _pensionPercentage,   // Percentage for pension (0-100)
        uint256 _retirementAge        // Retirement age for pension (60-75)
    ) external payable validUser(msg.sender) validAmount(msg.value) {
        require(_emergencyPercentage + _pensionPercentage <= 100, "Percentages exceed 100%");
        require(_retirementAge >= 60 && _retirementAge <= 75, "Invalid retirement age");
        
        uint256 totalAmount = msg.value;
        uint256 emergencyAmount = (totalAmount * _emergencyPercentage) / 100;
        uint256 pensionAmount = (totalAmount * _pensionPercentage) / 100;
        uint256 savingsAmount = totalAmount - emergencyAmount - pensionAmount;
        
        // Update user account
        UserAccount storage account = userAccounts[msg.sender];
        account.totalDeposited += totalAmount;
        account.currentBalance += savingsAmount;
        account.lastDepositTime = block.timestamp;
        
        // Handle emergency fund
        if (emergencyAmount > 0) {
            if (!emergencyFunds[msg.sender].isActive) {
                emergencyFunds[msg.sender] = EmergencyFund({
                    user: msg.sender,
                    balance: 0,
                    lastDepositTime: 0,
                    withdrawalCount: 0,
                    totalWithdrawn: 0,
                    isActive: true,
                    incentiveEarned: 0
                });
            }
            emergencyFunds[msg.sender].balance += emergencyAmount;
            emergencyFunds[msg.sender].lastDepositTime = block.timestamp;
            account.emergencyFund += emergencyAmount;
            totalEmergencyFunds += emergencyAmount;
        }
        
        // Handle pension fund
        if (pensionAmount > 0) {
            if (pensionVaults[msg.sender].vestingStartTime == 0) {
                pensionVaults[msg.sender] = PensionVault({
                    user: msg.sender,
                    totalDeposited: 0,
                    currentValue: 0,
                    retirementAge: _retirementAge,
                    vestingStartTime: block.timestamp,
                    vestingEndTime: block.timestamp + (10 * 365 days), // 10-year vesting
                    isVesting: true,
                    isWithdrawn: false,
                    monthlyWithdrawalAmount: 0,
                    lastWithdrawalTime: 0
                });
            }
            pensionVaults[msg.sender].totalDeposited += pensionAmount;
            pensionVaults[msg.sender].currentValue += pensionAmount;
            account.pensionFund += pensionAmount;
            totalPensionFunds += pensionAmount;
            
            emit PensionDeposit(msg.sender, pensionAmount, _retirementAge, block.timestamp);
        }
        
        // Process referral reward
        if (referrers[msg.sender] != address(0)) {
            uint256 referralReward = (totalAmount * referralBonusRate) / 10000;
            address referrer = referrers[msg.sender];
            
            userAccounts[referrer].totalReferralRewards += referralReward;
            totalReferralRewards += referralReward;
            
            referralHistory[referrer].push(ReferralData({
                referrer: referrer,
                referee: msg.sender,
                depositAmount: totalAmount,
                rewardAmount: referralReward,
                timestamp: block.timestamp,
                isPaid: false
            }));
            
            emit ReferralRewardPaid(referrer, msg.sender, referralReward, block.timestamp);
        }
        
        totalDeposited += totalAmount;
        
        emit DepositMade(msg.sender, totalAmount, emergencyAmount, pensionAmount, block.timestamp);
    }
    
    /**
     * Withdraw from regular savings
     */
    function withdrawSavings(uint256 _amount) external validUser(msg.sender) validAmount(_amount) {
        UserAccount storage account = userAccounts[msg.sender];
        require(account.currentBalance >= _amount, "Insufficient balance");
        
        account.currentBalance -= _amount;
        account.totalWithdrawn += _amount;
        account.lastWithdrawalTime = block.timestamp;
        
        totalWithdrawn += _amount;
        
        // Transfer ETH to user
        (bool success, ) = msg.sender.call{value: _amount}("");
        require(success, "Transfer failed");
        
        emit WithdrawalMade(msg.sender, _amount, 0, block.timestamp);
    }
    
    /**
     * Emergency withdrawal with fee
     */
    function emergencyWithdrawal(uint256 _amount) external validUser(msg.sender) validAmount(_amount) {
        EmergencyFund storage emergency = emergencyFunds[msg.sender];
        require(emergency.isActive, "No emergency fund");
        require(emergency.balance >= _amount, "Insufficient emergency balance");
        
        uint256 fee = (_amount * emergencyWithdrawalFee) / 10000;
        uint256 netAmount = _amount - fee;
        
        emergency.balance -= _amount;
        emergency.withdrawalCount++;
        emergency.totalWithdrawn += _amount;
        
        userAccounts[msg.sender].emergencyFund -= _amount;
        totalEmergencyFunds -= _amount;
        
        // Transfer net amount to user
        (bool success, ) = msg.sender.call{value: netAmount}("");
        require(success, "Transfer failed");
        
        emit EmergencyWithdrawal(msg.sender, _amount, fee, block.timestamp);
    }
    
    /**
     * Withdraw from pension vault (only after retirement age and vesting period)
     */
    function withdrawPension(uint256 _amount) external validUser(msg.sender) validAmount(_amount) {
        PensionVault storage pension = pensionVaults[msg.sender];
        require(pension.isVesting, "No pension vault");
        require(!pension.isWithdrawn, "Pension already withdrawn");
        require(block.timestamp >= pension.vestingEndTime, "Vesting period not completed");
        
        // Check retirement age (simplified - in production, use oracle for age verification)
        require(block.timestamp >= pension.vestingStartTime + (pension.retirementAge * 365 days), "Not retirement age");
        
        require(pension.currentValue >= _amount, "Insufficient pension balance");
        
        pension.currentValue -= _amount;
        pension.lastWithdrawalTime = block.timestamp;
        
        userAccounts[msg.sender].pensionFund -= _amount;
        totalPensionFunds -= _amount;
        
        // Transfer to user
        (bool success, ) = msg.sender.call{value: _amount}("");
        require(success, "Transfer failed");
        
        emit PensionWithdrawal(msg.sender, _amount, block.timestamp);
    }
    
    /**
     * Create a community pool (digital chama)
     */
    function createCommunityPool(string memory _name) external validUser(msg.sender) {
        CommunityPool storage pool = communityPools[nextCommunityPoolId];
        pool.id = nextCommunityPoolId;
        pool.creator = msg.sender;
        pool.name = _name;
        pool.totalDeposits = 0;
        pool.memberCount = 0;
        pool.yieldRate = 0;
        pool.isActive = true;
        pool.createdAt = block.timestamp;
        
        // Creator automatically becomes first member
        pool.isMember[msg.sender] = true;
        pool.memberCount = 1;
        
        totalCommunityPools++;
        
        emit CommunityPoolCreated(msg.sender, nextCommunityPoolId, _name, block.timestamp);
        nextCommunityPoolId++;
    }
    
    /**
     * Join a community pool
     */
    function joinCommunityPool(uint256 _poolId, uint256 _amount) external payable validUser(msg.sender) validAmount(_amount) {
        require(msg.value >= _amount, "Insufficient ETH sent");
        require(_poolId < nextCommunityPoolId, "Invalid pool ID");
        
        CommunityPool storage pool = communityPools[_poolId];
        require(pool.isActive, "Pool not active");
        require(!pool.isMember[msg.sender], "Already a member");
        require(pool.memberCount < 50, "Pool is full");
        
        pool.isMember[msg.sender] = true;
        pool.memberShares[msg.sender] = _amount;
        pool.totalDeposits += _amount;
        pool.memberCount++;
        
        userAccounts[msg.sender].communityPoolShares += _amount;
        
        emit CommunityPoolJoined(msg.sender, _poolId, _amount, block.timestamp);
    }
    
    /**
     * Purchase insurance policy
     */
    function purchaseInsurance(uint256 _coverageAmount) external validUser(msg.sender) validAmount(_coverageAmount) {
        require(!insurancePolicies[msg.sender].isActive, "Insurance already active");
        
        insurancePolicies[msg.sender] = InsurancePolicy({
            user: msg.sender,
            coverageAmount: _coverageAmount,
            premiumRate: insurancePremiumRate,
            coverageStartTime: block.timestamp,
            coverageEndTime: block.timestamp + (365 days), // 1 year coverage
            isActive: true,
            claimsCount: 0,
            totalClaimsPaid: 0
        });
        
        userAccounts[msg.sender].hasInsurance = true;
        totalInsurancePolicies++;
        
        emit InsurancePolicyPurchased(msg.sender, _coverageAmount, insurancePremiumRate, block.timestamp);
    }
    
    /**
     * Update user risk profile
     */
    function updateRiskProfile(uint256 _newProfile) external validUser(msg.sender) {
        require(_newProfile >= 1 && _newProfile <= 3, "Invalid risk profile");
        
        uint256 oldProfile = userAccounts[msg.sender].riskProfile;
        userAccounts[msg.sender].riskProfile = _newProfile;
        
        emit RiskProfileUpdated(msg.sender, oldProfile, _newProfile, block.timestamp);
    }
    
    /**
     * Update education score (called by education system)
     */
    function updateEducationScore(address _user, uint256 _newScore) external onlyOwner {
        require(userAccounts[_user].isActive, "User not registered");
        
        uint256 oldScore = userAccounts[_user].educationScore;
        userAccounts[_user].educationScore = _newScore;
        
        // Unlock premium features based on education score
        if (_newScore >= 100 && !userAccounts[_user].isPremiumUser) {
            userAccounts[_user].isPremiumUser = true;
            emit PremiumFeatureUnlocked(_user, "Premium User", block.timestamp);
        }
        
        emit EducationScoreUpdated(_user, oldScore, _newScore, block.timestamp);
    }
    
    /**
     * Get user's complete portfolio
     */
    function getUserPortfolio(address _user) external view returns (
        uint256 totalBalance,
        uint256 emergencyFund,
        uint256 pensionFund,
        uint256 communityShares,
        uint256 referralRewards,
        uint256 educationScore,
        uint256 riskProfile,
        bool hasInsurance,
        bool isPremiumUser
    ) {
        UserAccount memory account = userAccounts[_user];
        EmergencyFund memory emergency = emergencyFunds[_user];
        PensionVault memory pension = pensionVaults[_user];
        
        return (
            account.currentBalance,
            emergency.balance,
            pension.currentValue,
            account.communityPoolShares,
            account.totalReferralRewards,
            account.educationScore,
            account.riskProfile,
            account.hasInsurance,
            account.isPremiumUser
        );
    }
    
    /**
     * Get platform statistics
     */
    function getPlatformStats() external view returns (
        uint256 _totalUsers,
        uint256 _totalDeposited,
        uint256 _totalWithdrawn,
        uint256 _totalEmergencyFunds,
        uint256 _totalPensionFunds,
        uint256 _totalCommunityPools,
        uint256 _totalInsurancePolicies,
        uint256 _totalReferralRewards
    ) {
        return (
            totalUsers,
            totalDeposited,
            totalWithdrawn,
            totalEmergencyFunds,
            totalPensionFunds,
            totalCommunityPools,
            totalInsurancePolicies,
            totalReferralRewards
        );
    }
    
    /**
     * Owner functions for platform management
     */
    function setPlatformFeeRate(uint256 _newRate) external onlyOwner {
        require(_newRate <= 1000, "Fee rate too high");
        platformFeeRate = _newRate;
    }
    
    function setEmergencyWithdrawalFee(uint256 _newFee) external onlyOwner {
        require(_newFee <= 1000, "Fee too high");
        emergencyWithdrawalFee = _newFee;
    }
    
    function setReferralBonusRate(uint256 _newRate) external onlyOwner {
        require(_newRate <= 1000, "Rate too high");
        referralBonusRate = _newRate;
    }
    
    function withdrawPlatformFees() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No fees to withdraw");
        
        (bool success, ) = owner.call{value: balance}("");
        require(success, "Failed to withdraw fees");
    }
    
    receive() external payable {}
}

contracts/InclusiveFinanceTokenomics.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

/**
 * InclusiveFinanceTokenomics - Comprehensive tokenomics for $INCL governance token
 * Implements the complete economic model for the Inclusive Finance DApp
 * @author SafeNest Team
 */
contract InclusiveFinanceTokenomics {
    // Token Details
    string public constant name = "Inclusive Finance Token";
    string public constant symbol = "INCL";
    uint8 public constant decimals = 18;
    uint256 public constant TOTAL_SUPPLY = 1_000_000_000 * 10**18; // 1 billion tokens
    
    // Token Distribution (Total: 1B tokens)
    uint256 public constant COMMUNITY_ALLOCATION = 400_000_000 * 10**18; // 40% - Community rewards, referrals
    uint256 public constant TEAM_ALLOCATION = 150_000_000 * 10**18; // 15% - Team (4-year vesting)
    uint256 public constant TREASURY_ALLOCATION = 200_000_000 * 10**18; // 20% - Protocol treasury
    uint256 public constant LIQUIDITY_ALLOCATION = 100_000_000 * 10**18; // 10% - DEX liquidity
    uint256 public constant PARTNERS_ALLOCATION = 100_000_000 * 10**18; // 10% - Strategic partners
    uint256 public constant EARLY_ADOPTERS = 50_000_000 * 10**18; // 5% - Early users & testers
    
    // Staking Rewards
    uint256 public constant STAKING_REWARDS_POOL = 300_000_000 * 10**18; // 30% of total supply for staking rewards
    uint256 public constant REWARDS_DURATION = 4 * 365 days; // 4 years of rewards
    uint256 public constant REWARDS_PER_SECOND = STAKING_REWARDS_POOL / REWARDS_DURATION;
    
    // Fee Structure
    uint256 public constant PROTOCOL_FEE_RATE = 30; // 0.3% in basis points
    uint256 public constant STAKING_FEE_SHARE = 50; // 50% of fees go to stakers
    uint256 public constant TREASURY_FEE_SHARE = 30; // 30% to treasury
    uint256 public constant BURN_FEE_SHARE = 20; // 20% burned (deflationary)
    
    // Governance Parameters
    uint256 public constant MIN_STAKE_FOR_VOTING = 1000 * 10**18; // 1000 INCL minimum stake
    uint256 public constant VOTING_DURATION = 3 days;
    uint256 public constant EXECUTION_DELAY = 1 days;
    uint256 public constant QUORUM_THRESHOLD = 5; // 5% of total staked tokens
    
    // Referral System
    uint256 public constant REFERRAL_BONUS_RATE = 100; // 1% of referred user's deposits
    uint256 public constant REFERRAL_DURATION = 365 days; // 1 year of referral rewards
    uint256 public constant MAX_REFERRAL_DEPTH = 3; // 3 levels deep
    
    // Pension Vault Parameters
    uint256 public constant MIN_PENSION_AGE = 60; // Minimum retirement age
    uint256 public constant MAX_PENSION_AGE = 75; // Maximum retirement age
    uint256 public constant PENSION_VESTING_PERIOD = 10 * 365 days; // 10-year vesting
    uint256 public constant PENSION_YIELD_BOOST = 200; // 2% additional yield for pension deposits
    
    // Emergency Fund Parameters
    uint256 public constant EMERGENCY_WITHDRAWAL_FEE = 50; // 0.5% fee for instant withdrawal
    uint256 public constant EMERGENCY_INCENTIVE_RATE = 25; // 0.25% bonus for keeping funds parked
    
    // Mobile Money Integration
    uint256 public constant ROUND_UP_MIN_AMOUNT = 1 * 10**18; // 1 USDC minimum round-up
    uint256 public constant ROUND_UP_MAX_AMOUNT = 10 * 10**18; // 10 USDC maximum round-up
    uint256 public constant MOBILE_MONEY_FEE = 20; // 0.2% fee for mobile money integration
    
    // Insurance Parameters
    uint256 public constant INSURANCE_PREMIUM_RATE = 10; // 0.1% of yield for insurance
    uint256 public constant INSURANCE_COVERAGE_RATE = 1000; // 10x coverage for insured deposits
    
    // Community Pool Parameters
    uint256 public constant CHAMA_MIN_MEMBERS = 5;
    uint256 public constant CHAMA_MAX_MEMBERS = 50;
    uint256 public constant CHAMA_YIELD_BOOST = 100; // 1% additional yield for community pools
    
    // Inflation Protection
    uint256 public constant INFLATION_ADJUSTMENT_PERIOD = 30 days;
    uint256 public constant INFLATION_THRESHOLD = 500; // 5% inflation threshold
    
    // Cross-Border Transfer
    uint256 public constant CROSS_BORDER_FEE = 100; // 1% fee for cross-border transfers
    uint256 public constant CROSS_BORDER_MIN_AMOUNT = 100 * 10**18; // 100 USDC minimum
    
    // Premium Features (requires staking)
    uint256 public constant PREMIUM_WITHDRAWAL_STAKE = 10000 * 10**18; // 10k INCL for instant withdrawal
    uint256 public constant PREMIUM_TRANSFER_STAKE = 5000 * 10**18; // 5k INCL for free transfers
    uint256 public constant PREMIUM_INSURANCE_STAKE = 20000 * 10**18; // 20k INCL for premium insurance
    
    // Yield Strategy Allocation
    uint256 public constant CONSERVATIVE_ALLOCATION = 60; // 60% in low-risk strategies
    uint256 public constant MODERATE_ALLOCATION = 30; // 30% in moderate-risk strategies
    uint256 public constant AGGRESSIVE_ALLOCATION = 10; // 10% in higher-risk strategies
    
    // Risk Management
    uint256 public constant MAX_SINGLE_DEPOSIT = 10000 * 10**18; // 10k USDC max single deposit
    uint256 public constant MAX_DAILY_DEPOSIT = 50000 * 10**18; // 50k USDC max daily deposit
    uint256 public constant MAX_TOTAL_EXPOSURE = 1000000 * 10**18; // 1M USDC max total exposure
    
    // Liquidity Mining
    uint256 public constant LIQUIDITY_MINING_REWARDS = 50_000_000 * 10**18; // 50M tokens for liquidity mining
    uint256 public constant LIQUIDITY_MINING_DURATION = 2 * 365 days; // 2 years
    uint256 public constant LIQUIDITY_MINING_RATE = LIQUIDITY_MINING_REWARDS / LIQUIDITY_MINING_DURATION;
    
    // Educational Rewards
    uint256 public constant EDUCATION_REWARDS = 25_000_000 * 10**18; // 25M tokens for financial education
    uint256 public constant EDUCATION_COMPLETION_BONUS = 100 * 10**18; // 100 INCL per completed module
    
    // Partnership Rewards
    uint256 public constant PARTNER_REWARDS = 75_000_000 * 10**18; // 75M tokens for partner integrations
    uint256 public constant PARTNER_REFERRAL_BONUS = 500 * 10**18; // 500 INCL per partner referral
    
    // Events
    event TokenomicsInitialized(uint256 totalSupply, uint256 timestamp);
    event StakingRewardsUpdated(uint256 newRate, uint256 timestamp);
    event FeeStructureUpdated(uint256 protocolFee, uint256 stakingShare, uint256 timestamp);
    event ReferralBonusPaid(address referrer, address referee, uint256 amount, uint256 timestamp);
    event PensionDepositMade(address user, uint256 amount, uint256 retirementAge, uint256 timestamp);
    event EmergencyFundWithdrawal(address user, uint256 amount, uint256 fee, uint256 timestamp);
    event CommunityPoolCreated(address creator, uint256 poolId, uint256 memberCount, uint256 timestamp);
    event InsuranceClaimProcessed(address user, uint256 claimAmount, uint256 timestamp);
    event CrossBorderTransfer(address from, address to, uint256 amount, uint256 fee, uint256 timestamp);
    event PremiumFeatureUnlocked(address user, string feature, uint256 stakeAmount, uint256 timestamp);
    event LiquidityMiningReward(address user, uint256 amount, uint256 timestamp);
    event EducationRewardEarned(address user, string module, uint256 reward, uint256 timestamp);
    event PartnerRewardDistributed(address partner, uint256 amount, uint256 timestamp);
    
    // Constructor
    constructor() {
        emit TokenomicsInitialized(TOTAL_SUPPLY, block.timestamp);
    }
    
    // View functions for tokenomics parameters
    function getTokenomicsSummary() external pure returns (
        uint256 totalSupply,
        uint256 communityAllocation,
        uint256 stakingRewardsPool,
        uint256 protocolFeeRate,
        uint256 minStakeForVoting,
        uint256 referralBonusRate
    ) {
        return (
            TOTAL_SUPPLY,
            COMMUNITY_ALLOCATION,
            STAKING_REWARDS_POOL,
            PROTOCOL_FEE_RATE,
            MIN_STAKE_FOR_VOTING,
            REFERRAL_BONUS_RATE
        );
    }
    
    function getFeeStructure() external pure returns (
        uint256 protocolFee,
        uint256 stakingShare,
        uint256 treasuryShare,
        uint256 burnShare
    ) {
        return (
            PROTOCOL_FEE_RATE,
            STAKING_FEE_SHARE,
            TREASURY_FEE_SHARE,
            BURN_FEE_SHARE
        );
    }
    
    function getPensionParameters() external pure returns (
        uint256 minAge,
        uint256 maxAge,
        uint256 vestingPeriod,
        uint256 yieldBoost
    ) {
        return (
            MIN_PENSION_AGE,
            MAX_PENSION_AGE,
            PENSION_VESTING_PERIOD,
            PENSION_YIELD_BOOST
        );
    }
    
    function getEmergencyFundParameters() external pure returns (
        uint256 withdrawalFee,
        uint256 incentiveRate
    ) {
        return (
            EMERGENCY_WITHDRAWAL_FEE,
            EMERGENCY_INCENTIVE_RATE
        );
    }
    
    function getMobileMoneyParameters() external pure returns (
        uint256 minRoundUp,
        uint256 maxRoundUp,
        uint256 integrationFee
    ) {
        return (
            ROUND_UP_MIN_AMOUNT,
            ROUND_UP_MAX_AMOUNT,
            MOBILE_MONEY_FEE
        );
    }
    
    function getInsuranceParameters() external pure returns (
        uint256 premiumRate,
        uint256 coverageRate
    ) {
        return (
            INSURANCE_PREMIUM_RATE,
            INSURANCE_COVERAGE_RATE
        );
    }
    
    function getCommunityPoolParameters() external pure returns (
        uint256 minMembers,
        uint256 maxMembers,
        uint256 yieldBoost
    ) {
        return (
            CHAMA_MIN_MEMBERS,
            CHAMA_MAX_MEMBERS,
            CHAMA_YIELD_BOOST
        );
    }
    
    function getPremiumFeatureRequirements() external pure returns (
        uint256 instantWithdrawalStake,
        uint256 freeTransferStake,
        uint256 premiumInsuranceStake
    ) {
        return (
            PREMIUM_WITHDRAWAL_STAKE,
            PREMIUM_TRANSFER_STAKE,
            PREMIUM_INSURANCE_STAKE
        );
    }
    
    function getRiskManagementParameters() external pure returns (
        uint256 maxSingleDeposit,
        uint256 maxDailyDeposit,
        uint256 maxTotalExposure
    ) {
        return (
            MAX_SINGLE_DEPOSIT,
            MAX_DAILY_DEPOSIT,
            MAX_TOTAL_EXPOSURE
        );
    }
    
    function getRewardPools() external pure returns (
        uint256 liquidityMiningRewards,
        uint256 educationRewards,
        uint256 partnerRewards
    ) {
        return (
            LIQUIDITY_MINING_REWARDS,
            EDUCATION_REWARDS,
            PARTNER_REWARDS
        );
    }
}

contracts/MicroSavings.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

// import "hardhat/console.sol";

/**
 * MicroSavings - Mobile-first DeFi savings and investment platform
 * Enables micro-savings, micro-investments, and on-chain micro-pensions
 * @author SafeNest Team
 */
contract MicroSavings {
    // State Variables
    address public immutable owner;
    string public platformName = "MicroSavings - Mobile DeFi Platform";
    
    // Supported stablecoins
    mapping(address => bool) public supportedStablecoins;
    mapping(address => uint256) public stablecoinDecimals;
    
    // User savings accounts
    struct SavingsAccount {
        address user;
        address stablecoin;
        uint256 totalDeposited;
        uint256 totalWithdrawn;
        uint256 currentBalance;
        uint256 lastDepositTime;
        bool isActive;
    }
    
    // Investment strategies
    struct InvestmentStrategy {
        string name;
        address strategyContract;
        uint256 apy; // Annual percentage yield in basis points (100 = 1%)
        bool isActive;
        uint256 minDeposit;
        uint256 maxDeposit;
    }
    
    // User investment positions
    struct InvestmentPosition {
        address user;
        uint256 strategyId;
        address stablecoin;
        uint256 amount;
        uint256 shares;
        uint256 entryTime;
        bool isActive;
    }
    
    // Emergency fund vault
    struct EmergencyVault {
        address user;
        address stablecoin;
        uint256 balance;
        uint256 lastDepositTime;
        uint256 withdrawalCount;
        bool isActive;
    }
    
    // Goal tracking
    struct SavingsGoal {
        address user;
        string goalName;
        uint256 targetAmount;
        uint256 currentAmount;
        uint256 deadline;
        bool isCompleted;
        uint256 createdAt;
    }
    
    // Mappings
    mapping(address => SavingsAccount) public userSavingsAccounts;
    mapping(address => mapping(address => uint256)) public userStablecoinBalances;
    mapping(uint256 => InvestmentStrategy) public investmentStrategies;
    mapping(address => InvestmentPosition[]) public userInvestments;
    mapping(address => EmergencyVault) public userEmergencyVaults;
    mapping(address => SavingsGoal[]) public userGoals;
    
    // Platform statistics
    uint256 public totalUsers = 0;
    uint256 public totalDeposited = 0;
    uint256 public totalInvested = 0;
    uint256 public totalEmergencyFunds = 0;
    uint256 public platformFeeRate = 30; // 0.3% in basis points
    uint256 public nextStrategyId = 1;
    
    // Events
    event StablecoinAdded(address indexed stablecoin, uint256 decimals);
    event DepositMade(address indexed user, address indexed stablecoin, uint256 amount);
    event WithdrawalMade(address indexed user, address indexed stablecoin, uint256 amount);
    event InvestmentMade(address indexed user, uint256 strategyId, uint256 amount);
    event InvestmentWithdrawn(address indexed user, uint256 strategyId, uint256 amount);
    event EmergencyDeposit(address indexed user, address indexed stablecoin, uint256 amount);
    event EmergencyWithdrawal(address indexed user, address indexed stablecoin, uint256 amount);
    event GoalCreated(address indexed user, string goalName, uint256 targetAmount);
    event GoalUpdated(address indexed user, string goalName, uint256 currentAmount);
    event GoalCompleted(address indexed user, string goalName);
    
    // Modifiers
    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }
    
    modifier validStablecoin(address _stablecoin) {
        require(supportedStablecoins[_stablecoin], "Unsupported stablecoin");
        _;
    }
    
    modifier validAmount(uint256 _amount) {
        require(_amount > 0, "Amount must be greater than 0");
        _;
    }
    
    // Constructor
    constructor() {
        owner = msg.sender;
    }
    
    /**
     * Add supported stablecoin
     */
    function addStablecoin(address _stablecoin, uint256 _decimals) external onlyOwner {
        supportedStablecoins[_stablecoin] = true;
        stablecoinDecimals[_stablecoin] = _decimals;
        emit StablecoinAdded(_stablecoin, _decimals);
    }
    
    /**
     * Add investment strategy
     */
    function addInvestmentStrategy(
        string memory _name,
        address _strategyContract,
        uint256 _apy,
        uint256 _minDeposit,
        uint256 _maxDeposit
    ) external onlyOwner {
        investmentStrategies[nextStrategyId] = InvestmentStrategy({
            name: _name,
            strategyContract: _strategyContract,
            apy: _apy,
            isActive: true,
            minDeposit: _minDeposit,
            maxDeposit: _maxDeposit
        });
        nextStrategyId++;
    }
    
    /**
     * Make micro-savings deposit
     */
    function depositSavings(
        address _stablecoin,
        uint256 _amount
    ) external validStablecoin(_stablecoin) validAmount(_amount) {
        // Transfer stablecoin from user
        // Note: In production, you'd use IERC20(_stablecoin).transferFrom(msg.sender, address(this), _amount)
        
        // Update user savings account
        if (!userSavingsAccounts[msg.sender].isActive) {
            userSavingsAccounts[msg.sender] = SavingsAccount({
                user: msg.sender,
                stablecoin: _stablecoin,
                totalDeposited: 0,
                totalWithdrawn: 0,
                currentBalance: 0,
                lastDepositTime: 0,
                isActive: true
            });
            totalUsers++;
        }
        
        userSavingsAccounts[msg.sender].totalDeposited += _amount;
        userSavingsAccounts[msg.sender].currentBalance += _amount;
        userSavingsAccounts[msg.sender].lastDepositTime = block.timestamp;
        userStablecoinBalances[msg.sender][_stablecoin] += _amount;
        
        totalDeposited += _amount;
        
        emit DepositMade(msg.sender, _stablecoin, _amount);
    }
    
    /**
     * Withdraw from savings
     */
    function withdrawSavings(
        address _stablecoin,
        uint256 _amount
    ) external validStablecoin(_stablecoin) validAmount(_amount) {
        require(
            userStablecoinBalances[msg.sender][_stablecoin] >= _amount,
            "Insufficient balance"
        );
        
        userSavingsAccounts[msg.sender].totalWithdrawn += _amount;
        userSavingsAccounts[msg.sender].currentBalance -= _amount;
        userStablecoinBalances[msg.sender][_stablecoin] -= _amount;
        
        totalDeposited -= _amount;
        
        // Transfer stablecoin to user
        // Note: In production, you'd use IERC20(_stablecoin).transfer(msg.sender, _amount)
        
        emit WithdrawalMade(msg.sender, _stablecoin, _amount);
    }
    
    /**
     * Invest in DeFi strategy
     */
    function investInStrategy(
        uint256 _strategyId,
        address _stablecoin,
        uint256 _amount
    ) external validStablecoin(_stablecoin) validAmount(_amount) {
        InvestmentStrategy storage strategy = investmentStrategies[_strategyId];
        require(strategy.isActive, "Strategy not active");
        require(_amount >= strategy.minDeposit, "Amount below minimum");
        require(_amount <= strategy.maxDeposit, "Amount above maximum");
        require(
            userStablecoinBalances[msg.sender][_stablecoin] >= _amount,
            "Insufficient balance"
        );
        
        // Calculate shares (simplified - in production, use proper share calculation)
        uint256 shares = _amount * 1e18 / 1e18; // 1:1 for simplicity
        
        userInvestments[msg.sender].push(InvestmentPosition({
            user: msg.sender,
            strategyId: _strategyId,
            stablecoin: _stablecoin,
            amount: _amount,
            shares: shares,
            entryTime: block.timestamp,
            isActive: true
        }));
        
        userStablecoinBalances[msg.sender][_stablecoin] -= _amount;
        totalInvested += _amount;
        
        emit InvestmentMade(msg.sender, _strategyId, _amount);
    }
    
    /**
     * Withdraw from investment
     */
    function withdrawFromInvestment(
        uint256 _positionIndex,
        uint256 _shares
    ) external validAmount(_shares) {
        require(
            _positionIndex < userInvestments[msg.sender].length,
            "Invalid position index"
        );
        
        InvestmentPosition storage position = userInvestments[msg.sender][_positionIndex];
        require(position.isActive, "Position not active");
        require(position.shares >= _shares, "Insufficient shares");
        
        // Calculate withdrawal amount (simplified)
        uint256 withdrawalAmount = position.amount * _shares / position.shares;
        
        position.shares -= _shares;
        position.amount -= withdrawalAmount;
        
        if (position.shares == 0) {
            position.isActive = false;
        }
        
        userStablecoinBalances[msg.sender][position.stablecoin] += withdrawalAmount;
        totalInvested -= withdrawalAmount;
        
        emit InvestmentWithdrawn(msg.sender, position.strategyId, withdrawalAmount);
    }
    
    /**
     * Deposit to emergency vault
     */
    function depositEmergencyFund(
        address _stablecoin,
        uint256 _amount
    ) external validStablecoin(_stablecoin) validAmount(_amount) {
        require(
            userStablecoinBalances[msg.sender][_stablecoin] >= _amount,
            "Insufficient balance"
        );
        
        if (!userEmergencyVaults[msg.sender].isActive) {
            userEmergencyVaults[msg.sender] = EmergencyVault({
                user: msg.sender,
                stablecoin: _stablecoin,
                balance: 0,
                lastDepositTime: 0,
                withdrawalCount: 0,
                isActive: true
            });
        }
        
        userEmergencyVaults[msg.sender].balance += _amount;
        userEmergencyVaults[msg.sender].lastDepositTime = block.timestamp;
        userStablecoinBalances[msg.sender][_stablecoin] -= _amount;
        
        totalEmergencyFunds += _amount;
        
        emit EmergencyDeposit(msg.sender, _stablecoin, _amount);
    }
    
    /**
     * Withdraw from emergency vault (instant)
     */
    function withdrawEmergencyFund(
        address _stablecoin,
        uint256 _amount
    ) external validStablecoin(_stablecoin) validAmount(_amount) {
        require(userEmergencyVaults[msg.sender].isActive, "No emergency vault");
        require(
            userEmergencyVaults[msg.sender].balance >= _amount,
            "Insufficient emergency balance"
        );
        
        userEmergencyVaults[msg.sender].balance -= _amount;
        userEmergencyVaults[msg.sender].withdrawalCount++;
        userStablecoinBalances[msg.sender][_stablecoin] += _amount;
        
        totalEmergencyFunds -= _amount;
        
        emit EmergencyWithdrawal(msg.sender, _stablecoin, _amount);
    }
    
    /**
     * Create savings goal
     */
    function createSavingsGoal(
        string memory _goalName,
        uint256 _targetAmount,
        uint256 _deadline
    ) external validAmount(_targetAmount) {
        require(_deadline > block.timestamp, "Deadline must be in the future");
        
        userGoals[msg.sender].push(SavingsGoal({
            user: msg.sender,
            goalName: _goalName,
            targetAmount: _targetAmount,
            currentAmount: 0,
            deadline: _deadline,
            isCompleted: false,
            createdAt: block.timestamp
        }));
        
        emit GoalCreated(msg.sender, _goalName, _targetAmount);
    }
    
    /**
     * Update goal progress
     */
    function updateGoalProgress(
        uint256 _goalIndex,
        uint256 _additionalAmount
    ) external validAmount(_additionalAmount) {
        require(_goalIndex < userGoals[msg.sender].length, "Invalid goal index");
        
        SavingsGoal storage goal = userGoals[msg.sender][_goalIndex];
        require(!goal.isCompleted, "Goal already completed");
        
        goal.currentAmount += _additionalAmount;
        
        if (goal.currentAmount >= goal.targetAmount) {
            goal.isCompleted = true;
            emit GoalCompleted(msg.sender, goal.goalName);
        }
        
        emit GoalUpdated(msg.sender, goal.goalName, goal.currentAmount);
    }
    
    /**
     * Get user's total portfolio value
     */
    function getPortfolioValue(address _user) external view returns (
        uint256 totalSavings,
        uint256 totalInvestments,
        uint256 totalEmergency,
        uint256 totalValue
    ) {
        totalSavings = userSavingsAccounts[_user].currentBalance;
        totalEmergency = userEmergencyVaults[_user].balance;
        
        // Calculate total investments (simplified to avoid stack too deep)
        InvestmentPosition[] memory investments = userInvestments[_user];
        for (uint256 i = 0; i < investments.length; i++) {
            if (investments[i].isActive) {
                totalInvestments += investments[i].amount;
            }
        }
        
        totalValue = totalSavings + totalInvestments + totalEmergency;
    }
    
    /**
     * Get platform statistics
     */
    function getPlatformStats() external view returns (
        uint256 _totalUsers,
        uint256 _totalDeposited,
        uint256 _totalInvested,
        uint256 _totalEmergencyFunds,
        uint256 _platformFeeRate
    ) {
        return (totalUsers, totalDeposited, totalInvested, totalEmergencyFunds, platformFeeRate);
    }
    
    /**
     * Set platform fee rate (only owner)
     */
    function setPlatformFeeRate(uint256 _newRate) external onlyOwner {
        require(_newRate <= 1000, "Fee rate too high"); // Max 10%
        platformFeeRate = _newRate;
    }
    
    /**
     * Emergency function to pause platform (only owner)
     */
    function emergencyPause() external onlyOwner {
        // In production, implement pausable functionality
        // This would prevent new deposits/withdrawals
    }
}

contracts/SAFEToken.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

/**
 * SAFEToken - Governance token for SafeNest platform
 * Enables premium features, governance, and fee sharing
 * @author SafeNest Team
 */
contract SAFEToken {
    // Token Details
    string public constant name = "SafeNest Token";
    string public constant symbol = "SAFE";
    uint8 public constant decimals = 18;
    uint256 public constant TOTAL_SUPPLY = 1_000_000_000 * 10**18; // 1 billion tokens
    
    // Token Distribution
    uint256 public constant COMMUNITY_ALLOCATION = 400_000_000 * 10**18; // 40%
    uint256 public constant TEAM_ALLOCATION = 150_000_000 * 10**18; // 15%
    uint256 public constant TREASURY_ALLOCATION = 200_000_000 * 10**18; // 20%
    uint256 public constant LIQUIDITY_ALLOCATION = 100_000_000 * 10**18; // 10%
    uint256 public constant PARTNERS_ALLOCATION = 100_000_000 * 10**18; // 10%
    uint256 public constant EARLY_ADOPTERS = 50_000_000 * 10**18; // 5%
    
    // Staking Parameters
    uint256 public constant STAKING_REWARDS_POOL = 300_000_000 * 10**18; // 30% for staking rewards
    uint256 public constant REWARDS_DURATION = 4 * 365 days; // 4 years
    uint256 public constant REWARDS_PER_SECOND = STAKING_REWARDS_POOL / REWARDS_DURATION;
    
    // Premium Feature Requirements
    uint256 public constant PREMIUM_VAULT_STAKE = 10000 * 10**18; // 10k SAFE for premium vaults
    uint256 public constant PREMIUM_WITHDRAWAL_STAKE = 5000 * 10**18; // 5k SAFE for instant withdrawal
    uint256 public constant PREMIUM_TRANSFER_STAKE = 2000 * 10**18; // 2k SAFE for free transfers
    uint256 public constant PREMIUM_INSURANCE_STAKE = 15000 * 10**18; // 15k SAFE for premium insurance
    
    // Governance Parameters
    uint256 public constant MIN_STAKE_FOR_VOTING = 1000 * 10**18; // 1k SAFE minimum
    uint256 public constant VOTING_DURATION = 3 days;
    uint256 public constant EXECUTION_DELAY = 1 days;
    uint256 public constant QUORUM_THRESHOLD = 5; // 5% of total staked
    
    // Referral System
    uint256 public constant REFERRAL_BONUS_RATE = 100; // 1% of referred user's deposits
    uint256 public constant REFERRAL_DURATION = 365 days; // 1 year
    uint256 public constant MAX_REFERRAL_DEPTH = 3; // 3 levels deep
    
    // Community Rewards
    uint256 public constant COMMUNITY_REWARDS_POOL = 100_000_000 * 10**18; // 100M for community rewards
    uint256 public constant EDUCATION_REWARD = 100 * 10**18; // 100 SAFE per education module
    uint256 public constant POOL_CREATION_REWARD = 500 * 10**18; // 500 SAFE for creating pools
    uint256 public constant ACTIVE_USER_REWARD = 50 * 10**18; // 50 SAFE for active users
    
    // Protocol Fee Structure
    uint256 public constant MIN_PROTOCOL_FEE = 10; // 0.1% minimum
    uint256 public constant MAX_PROTOCOL_FEE = 30; // 0.3% maximum
    uint256 public constant DEFAULT_PROTOCOL_FEE = 20; // 0.2% default
    
    // Fee Distribution
    uint256 public constant STAKERS_FEE_SHARE = 60; // 60% to stakers
    uint256 public constant TREASURY_FEE_SHARE = 25; // 25% to treasury
    uint256 public constant BURN_FEE_SHARE = 15; // 15% burned (deflationary)
    
    // State Variables
    address public immutable owner;
    address public platformContract;
    
    mapping(address => uint256) public balanceOf;
    mapping(address => mapping(address => uint256)) public allowance;
    mapping(address => bool) public isMinter;
    
    // Staking
    struct Stake {
        uint256 amount;
        uint256 timestamp;
        uint256 lockPeriod;
        bool isActive;
    }
    
    mapping(address => Stake[]) public userStakes;
    mapping(address => uint256) public totalStaked;
    uint256 public totalStakedSupply = 0;
    
    // Governance
    struct Proposal {
        uint256 id;
        address proposer;
        string title;
        string description;
        uint256 startTime;
        uint256 endTime;
        uint256 forVotes;
        uint256 againstVotes;
        bool executed;
        mapping(address => bool) hasVoted;
    }
    
    mapping(uint256 => Proposal) public proposals;
    uint256 public nextProposalId = 1;
    
    // Referral System
    mapping(address => address) public referrers;
    mapping(address => uint256) public referralCount;
    mapping(address => uint256) public totalReferralRewards;
    
    // Community Rewards
    mapping(address => uint256) public educationScore;
    mapping(address => uint256) public communityRewards;
    mapping(address => bool) public hasCreatedPool;
    mapping(address => uint256) public lastActiveTime;
    
    // Events
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
    event Staked(address indexed user, uint256 amount, uint256 lockPeriod);
    event Unstaked(address indexed user, uint256 amount);
    event ProposalCreated(uint256 indexed proposalId, address indexed proposer, string title);
    event VoteCast(uint256 indexed proposalId, address indexed voter, bool support, uint256 votes);
    event ProposalExecuted(uint256 indexed proposalId);
    event ReferralRewardPaid(address indexed referrer, address indexed referee, uint256 amount);
    event CommunityRewardEarned(address indexed user, string rewardType, uint256 amount);
    event PremiumFeatureUnlocked(address indexed user, string feature, uint256 stakeAmount);
    event ProtocolFeeUpdated(uint256 oldFee, uint256 newFee);
    event FeeDistributed(uint256 stakersAmount, uint256 treasuryAmount, uint256 burnAmount);
    
    // Modifiers
    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }
    
    modifier onlyMinter() {
        require(isMinter[msg.sender] || msg.sender == owner, "Not a minter");
        _;
    }
    
    modifier validAddress(address _addr) {
        require(_addr != address(0), "Invalid address");
        _;
    }
    
    // Constructor
    constructor() {
        owner = msg.sender;
        balanceOf[owner] = TOTAL_SUPPLY;
        emit Transfer(address(0), owner, TOTAL_SUPPLY);
    }
    
    // ERC20 Functions
    function transfer(address _to, uint256 _value) external returns (bool) {
        require(_to != address(0), "Transfer to zero address");
        require(balanceOf[msg.sender] >= _value, "Insufficient balance");
        
        balanceOf[msg.sender] -= _value;
        balanceOf[_to] += _value;
        
        emit Transfer(msg.sender, _to, _value);
        return true;
    }
    
    function approve(address _spender, uint256 _value) external returns (bool) {
        allowance[msg.sender][_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }
    
    function transferFrom(address _from, address _to, uint256 _value) external returns (bool) {
        require(_to != address(0), "Transfer to zero address");
        require(balanceOf[_from] >= _value, "Insufficient balance");
        require(allowance[_from][msg.sender] >= _value, "Insufficient allowance");
        
        balanceOf[_from] -= _value;
        balanceOf[_to] += _value;
        allowance[_from][msg.sender] -= _value;
        
        emit Transfer(_from, _to, _value);
        return true;
    }
    
    // Staking Functions
    function stake(uint256 _amount, uint256 _lockPeriod) external {
        require(balanceOf[msg.sender] >= _amount, "Insufficient balance");
        require(_lockPeriod >= 30 days, "Minimum lock period is 30 days");
        require(_lockPeriod <= 365 days, "Maximum lock period is 1 year");
        
        balanceOf[msg.sender] -= _amount;
        totalStaked[msg.sender] += _amount;
        totalStakedSupply += _amount;
        
        userStakes[msg.sender].push(Stake({
            amount: _amount,
            timestamp: block.timestamp,
            lockPeriod: _lockPeriod,
            isActive: true
        }));
        
        emit Staked(msg.sender, _amount, _lockPeriod);
    }
    
    function unstake(uint256 _stakeIndex) external {
        require(_stakeIndex < userStakes[msg.sender].length, "Invalid stake index");
        
        Stake storage userStake = userStakes[msg.sender][_stakeIndex];
        require(userStake.isActive, "Stake not active");
        require(
            block.timestamp >= userStake.timestamp + userStake.lockPeriod,
            "Stake still locked"
        );
        
        userStake.isActive = false;
        totalStaked[msg.sender] -= userStake.amount;
        totalStakedSupply -= userStake.amount;
        balanceOf[msg.sender] += userStake.amount;
        
        emit Unstaked(msg.sender, userStake.amount);
    }
    
    // Governance Functions
    function createProposal(
        string memory _title,
        string memory _description
    ) external {
        require(totalStaked[msg.sender] >= MIN_STAKE_FOR_VOTING, "Insufficient stake for proposal");
        
        Proposal storage proposal = proposals[nextProposalId];
        proposal.id = nextProposalId;
        proposal.proposer = msg.sender;
        proposal.title = _title;
        proposal.description = _description;
        proposal.startTime = block.timestamp;
        proposal.endTime = block.timestamp + VOTING_DURATION;
        proposal.forVotes = 0;
        proposal.againstVotes = 0;
        proposal.executed = false;
        
        emit ProposalCreated(nextProposalId, msg.sender, _title);
        nextProposalId++;
    }
    
    function vote(uint256 _proposalId, bool _support) external {
        require(_proposalId < nextProposalId, "Invalid proposal");
        Proposal storage proposal = proposals[_proposalId];
        require(block.timestamp >= proposal.startTime, "Voting not started");
        require(block.timestamp <= proposal.endTime, "Voting ended");
        require(!proposal.hasVoted[msg.sender], "Already voted");
        require(totalStaked[msg.sender] > 0, "No stake to vote");
        
        proposal.hasVoted[msg.sender] = true;
        uint256 votes = totalStaked[msg.sender];
        
        if (_support) {
            proposal.forVotes += votes;
        } else {
            proposal.againstVotes += votes;
        }
        
        emit VoteCast(_proposalId, msg.sender, _support, votes);
    }
    
    function executeProposal(uint256 _proposalId) external {
        require(_proposalId < nextProposalId, "Invalid proposal");
        Proposal storage proposal = proposals[_proposalId];
        require(block.timestamp > proposal.endTime, "Voting not ended");
        require(!proposal.executed, "Already executed");
        require(proposal.forVotes > proposal.againstVotes, "Proposal rejected");
        require(proposal.forVotes >= (totalStakedSupply * QUORUM_THRESHOLD) / 100, "Quorum not met");
        
        proposal.executed = true;
        emit ProposalExecuted(_proposalId);
    }
    
    // Referral System
    function setReferrer(address _referrer) external {
        require(_referrer != address(0), "Invalid referrer");
        require(_referrer != msg.sender, "Cannot refer yourself");
        require(referrers[msg.sender] == address(0), "Referrer already set");
        
        referrers[msg.sender] = _referrer;
        referralCount[_referrer]++;
    }
    
    function payReferralReward(address _referee, uint256 _depositAmount) external onlyMinter {
        address referrer = referrers[_referee];
        if (referrer != address(0)) {
            uint256 reward = (_depositAmount * REFERRAL_BONUS_RATE) / 10000;
            totalReferralRewards[referrer] += reward;
            balanceOf[referrer] += reward;
            
            emit ReferralRewardPaid(referrer, _referee, reward);
        }
    }
    
    // Community Rewards
    function earnEducationReward(address _user) external onlyMinter {
        educationScore[_user] += 100;
        communityRewards[_user] += EDUCATION_REWARD;
        balanceOf[_user] += EDUCATION_REWARD;
        
        emit CommunityRewardEarned(_user, "Education", EDUCATION_REWARD);
    }
    
    function earnPoolCreationReward(address _user) external onlyMinter {
        if (!hasCreatedPool[_user]) {
            hasCreatedPool[_user] = true;
            communityRewards[_user] += POOL_CREATION_REWARD;
            balanceOf[_user] += POOL_CREATION_REWARD;
            
            emit CommunityRewardEarned(_user, "Pool Creation", POOL_CREATION_REWARD);
        }
    }
    
    function earnActiveUserReward(address _user) external onlyMinter {
        if (block.timestamp - lastActiveTime[_user] >= 7 days) {
            lastActiveTime[_user] = block.timestamp;
            communityRewards[_user] += ACTIVE_USER_REWARD;
            balanceOf[_user] += ACTIVE_USER_REWARD;
            
            emit CommunityRewardEarned(_user, "Active User", ACTIVE_USER_REWARD);
        }
    }
    
    // Premium Features
    function checkPremiumVaultAccess(address _user) external view returns (bool) {
        return totalStaked[_user] >= PREMIUM_VAULT_STAKE;
    }
    
    function checkPremiumWithdrawalAccess(address _user) external view returns (bool) {
        return totalStaked[_user] >= PREMIUM_WITHDRAWAL_STAKE;
    }
    
    function checkPremiumTransferAccess(address _user) external view returns (bool) {
        return totalStaked[_user] >= PREMIUM_TRANSFER_STAKE;
    }
    
    function checkPremiumInsuranceAccess(address _user) external view returns (bool) {
        return totalStaked[_user] >= PREMIUM_INSURANCE_STAKE;
    }
    
    // Admin Functions
    function setPlatformContract(address _platformContract) external onlyOwner {
        platformContract = _platformContract;
        isMinter[_platformContract] = true;
    }
    
    function setMinter(address _minter, bool _status) external onlyOwner {
        isMinter[_minter] = _status;
    }
    
    function mint(address _to, uint256 _amount) external onlyMinter {
        balanceOf[_to] += _amount;
        emit Transfer(address(0), _to, _amount);
    }
    
    // View Functions
    function getStakingInfo(address _user) external view returns (
        uint256 totalStakedAmount,
        uint256 activeStakes,
        uint256 totalRewards
    ) {
        return (
            totalStaked[_user],
            userStakes[_user].length,
            communityRewards[_user]
        );
    }
    
    function getGovernanceInfo() external view returns (
        uint256 totalStakedSupplyValue,
        uint256 minStakeForVoting,
        uint256 quorumThreshold
    ) {
        return (
            totalStakedSupply,
            MIN_STAKE_FOR_VOTING,
            QUORUM_THRESHOLD
        );
    }
    
    function getReferralInfo(address _user) external view returns (
        address referrer,
        uint256 userReferralCount,
        uint256 totalRewards
    ) {
        return (
            referrers[_user],
            referralCount[_user],
            totalReferralRewards[_user]
        );
    }
}

contracts/SimpleMicroSavings.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

// import "hardhat/console.sol";

/**
 * SimpleMicroSavings - Simplified mobile-first DeFi savings platform
 * @author SafeNest Team
 */
contract SimpleMicroSavings {
    // State Variables
    address public immutable owner;
    string public platformName = "SimpleMicroSavings - Mobile DeFi Platform";
    
    // User savings accounts
    struct SavingsAccount {
        address user;
        uint256 totalDeposited;
        uint256 totalWithdrawn;
        uint256 currentBalance;
        uint256 lastDepositTime;
        bool isActive;
    }
    
    // Investment strategies
    struct InvestmentStrategy {
        string name;
        uint256 apy; // Annual percentage yield in basis points
        bool isActive;
        uint256 minDeposit;
        uint256 maxDeposit;
    }
    
    // User investment positions
    struct InvestmentPosition {
        address user;
        uint256 strategyId;
        uint256 amount;
        uint256 entryTime;
        bool isActive;
    }
    
    // Emergency fund vault
    struct EmergencyVault {
        address user;
        uint256 balance;
        uint256 lastDepositTime;
        bool isActive;
    }
    
    // Mappings
    mapping(address => SavingsAccount) public userSavingsAccounts;
    mapping(uint256 => InvestmentStrategy) public investmentStrategies;
    mapping(address => InvestmentPosition[]) public userInvestments;
    mapping(address => EmergencyVault) public userEmergencyVaults;
    
    // Platform statistics
    uint256 public totalUsers = 0;
    uint256 public totalDeposited = 0;
    uint256 public totalInvested = 0;
    uint256 public totalEmergencyFunds = 0;
    uint256 public platformFeeRate = 30; // 0.3% in basis points
    uint256 public nextStrategyId = 1;
    
    // Events
    event DepositMade(address indexed user, uint256 amount);
    event WithdrawalMade(address indexed user, uint256 amount);
    event InvestmentMade(address indexed user, uint256 strategyId, uint256 amount);
    event EmergencyDeposit(address indexed user, uint256 amount);
    event EmergencyWithdrawal(address indexed user, uint256 amount);
    
    // Modifiers
    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }
    
    modifier validAmount(uint256 _amount) {
        require(_amount > 0, "Amount must be greater than 0");
        _;
    }
    
    // Constructor
    constructor() {
        owner = msg.sender;
    }
    
    /**
     * Add investment strategy
     */
    function addInvestmentStrategy(
        string memory _name,
        uint256 _apy,
        uint256 _minDeposit,
        uint256 _maxDeposit
    ) external onlyOwner {
        investmentStrategies[nextStrategyId] = InvestmentStrategy({
            name: _name,
            apy: _apy,
            isActive: true,
            minDeposit: _minDeposit,
            maxDeposit: _maxDeposit
        });
        nextStrategyId++;
    }
    
    /**
     * Make micro-savings deposit
     */
    function depositSavings(uint256 _amount) external validAmount(_amount) payable {
        require(msg.value >= _amount, "Insufficient ETH sent");
        
        // Update user savings account
        if (!userSavingsAccounts[msg.sender].isActive) {
            userSavingsAccounts[msg.sender] = SavingsAccount({
                user: msg.sender,
                totalDeposited: 0,
                totalWithdrawn: 0,
                currentBalance: 0,
                lastDepositTime: 0,
                isActive: true
            });
            totalUsers++;
        }
        
        userSavingsAccounts[msg.sender].totalDeposited += _amount;
        userSavingsAccounts[msg.sender].currentBalance += _amount;
        userSavingsAccounts[msg.sender].lastDepositTime = block.timestamp;
        
        totalDeposited += _amount;
        
        emit DepositMade(msg.sender, _amount);
    }
    
    /**
     * Withdraw from savings
     */
    function withdrawSavings(uint256 _amount) external validAmount(_amount) {
        require(
            userSavingsAccounts[msg.sender].currentBalance >= _amount,
            "Insufficient balance"
        );
        
        userSavingsAccounts[msg.sender].totalWithdrawn += _amount;
        userSavingsAccounts[msg.sender].currentBalance -= _amount;
        
        totalDeposited -= _amount;
        
        // Transfer ETH to user
        (bool success, ) = msg.sender.call{value: _amount}("");
        require(success, "Transfer failed");
        
        emit WithdrawalMade(msg.sender, _amount);
    }
    
    /**
     * Invest in DeFi strategy
     */
    function investInStrategy(
        uint256 _strategyId,
        uint256 _amount
    ) external validAmount(_amount) payable {
        InvestmentStrategy storage strategy = investmentStrategies[_strategyId];
        require(strategy.isActive, "Strategy not active");
        require(_amount >= strategy.minDeposit, "Amount below minimum");
        require(_amount <= strategy.maxDeposit, "Amount above maximum");
        require(msg.value >= _amount, "Insufficient ETH sent");
        
        userInvestments[msg.sender].push(InvestmentPosition({
            user: msg.sender,
            strategyId: _strategyId,
            amount: _amount,
            entryTime: block.timestamp,
            isActive: true
        }));
        
        totalInvested += _amount;
        
        emit InvestmentMade(msg.sender, _strategyId, _amount);
    }
    
    /**
     * Deposit to emergency vault
     */
    function depositEmergencyFund(uint256 _amount) external validAmount(_amount) payable {
        require(msg.value >= _amount, "Insufficient ETH sent");
        
        if (!userEmergencyVaults[msg.sender].isActive) {
            userEmergencyVaults[msg.sender] = EmergencyVault({
                user: msg.sender,
                balance: 0,
                lastDepositTime: 0,
                isActive: true
            });
        }
        
        userEmergencyVaults[msg.sender].balance += _amount;
        userEmergencyVaults[msg.sender].lastDepositTime = block.timestamp;
        
        totalEmergencyFunds += _amount;
        
        emit EmergencyDeposit(msg.sender, _amount);
    }
    
    /**
     * Withdraw from emergency vault (instant)
     */
    function withdrawEmergencyFund(uint256 _amount) external validAmount(_amount) {
        require(userEmergencyVaults[msg.sender].isActive, "No emergency vault");
        require(
            userEmergencyVaults[msg.sender].balance >= _amount,
            "Insufficient emergency balance"
        );
        
        userEmergencyVaults[msg.sender].balance -= _amount;
        
        totalEmergencyFunds -= _amount;
        
        // Transfer ETH to user
        (bool success, ) = msg.sender.call{value: _amount}("");
        require(success, "Transfer failed");
        
        emit EmergencyWithdrawal(msg.sender, _amount);
    }
    
    /**
     * Get user's total portfolio value
     */
    function getPortfolioValue(address _user) external view returns (
        uint256 totalSavings,
        uint256 totalInvestments,
        uint256 totalEmergency,
        uint256 totalValue
    ) {
        totalSavings = userSavingsAccounts[_user].currentBalance;
        totalEmergency = userEmergencyVaults[_user].balance;
        
        // Calculate total investments
        InvestmentPosition[] memory investments = userInvestments[_user];
        for (uint256 i = 0; i < investments.length; i++) {
            if (investments[i].isActive) {
                totalInvestments += investments[i].amount;
            }
        }
        
        totalValue = totalSavings + totalInvestments + totalEmergency;
    }
    
    /**
     * Get platform statistics
     */
    function getPlatformStats() external view returns (
        uint256 _totalUsers,
        uint256 _totalDeposited,
        uint256 _totalInvested,
        uint256 _totalEmergencyFunds,
        uint256 _platformFeeRate
    ) {
        return (totalUsers, totalDeposited, totalInvested, totalEmergencyFunds, platformFeeRate);
    }
    
    /**
     * Set platform fee rate (only owner)
     */
    function setPlatformFeeRate(uint256 _newRate) external onlyOwner {
        require(_newRate <= 1000, "Fee rate too high"); // Max 10%
        platformFeeRate = _newRate;
    }
    
    /**
     * Withdraw platform fees (only owner)
     */
    function withdrawFees() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No fees to withdraw");
        
        (bool success, ) = owner.call{value: balance}("");
        require(success, "Failed to withdraw fees");
    }
    
    receive() external payable {}
}

contracts/VaultSystem.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

/**
 * VaultSystem - Comprehensive vault system for SafeNest platform
 * Implements micro-savings, pension nest, and emergency vaults
 * @author SafeNest Team
 */
contract VaultSystem {
    // State Variables
    address public immutable owner;
    address public safeToken;
    string public platformName = "SafeNest Vault System";
    
    // Vault Types
    enum VaultType {
        MICRO_SAVINGS,    // Daily/weekly micro-savings
        PENSION_NEST,     // Long-term retirement savings
        EMERGENCY_VAULT   // Liquid emergency fund
    }
    
    // User Vault Structure
    struct UserVault {
        address user;
        VaultType vaultType;
        uint256 totalDeposited;
        uint256 currentBalance;
        uint256 totalWithdrawn;
        uint256 lastDepositTime;
        uint256 lastWithdrawalTime;
        uint256 yieldEarned;
        bool isActive;
        uint256 autoDepositAmount;
        uint256 autoDepositFrequency; // in days
        uint256 nextAutoDeposit;
    }
    
    // Yield Strategy Structure
    struct YieldStrategy {
        uint256 id;
        string name;
        address strategyContract;
        uint256 apy;
        uint256 riskLevel; // 1=Low, 2=Medium, 3=High
        bool isActive;
        uint256 minDeposit;
        uint256 maxDeposit;
        uint256 totalDeposited;
    }
    
    // Insurance Policy Structure
    struct InsurancePolicy {
        address user;
        uint256 coverageAmount;
        uint256 premiumRate;
        uint256 coverageStartTime;
        uint256 coverageEndTime;
        bool isActive;
        uint256 claimsCount;
        uint256 totalClaimsPaid;
    }
    
    // Mappings
    mapping(address => mapping(VaultType => UserVault)) public userVaults;
    mapping(uint256 => YieldStrategy) public yieldStrategies;
    mapping(address => InsurancePolicy) public insurancePolicies;
    mapping(address => bool) public hasPremiumAccess;
    
    // Platform Statistics
    uint256 public totalUsers = 0;
    uint256 public totalDeposited = 0;
    uint256 public totalWithdrawn = 0;
    uint256 public totalYieldEarned = 0;
    uint256 public totalInsurancePolicies = 0;
    
    // Platform Parameters
    uint256 public protocolFeeRate = 20; // 0.2% in basis points
    uint256 public emergencyWithdrawalFee = 50; // 0.5%
    uint256 public pensionYieldBoost = 200; // 2%
    uint256 public microSavingsYieldBoost = 100; // 1%
    uint256 public emergencyIncentiveRate = 25; // 0.25%
    
    // Counters
    uint256 public nextYieldStrategyId = 1;
    
    // Events
    event VaultCreated(address indexed user, VaultType vaultType, uint256 timestamp);
    event DepositMade(address indexed user, VaultType vaultType, uint256 amount, uint256 timestamp);
    event WithdrawalMade(address indexed user, VaultType vaultType, uint256 amount, uint256 fee, uint256 timestamp);
    event AutoDepositEnabled(address indexed user, VaultType vaultType, uint256 amount, uint256 frequency);
    event AutoDepositExecuted(address indexed user, VaultType vaultType, uint256 amount, uint256 timestamp);
    event YieldStrategyAdded(uint256 indexed strategyId, string name, uint256 apy, uint256 riskLevel);
    event YieldEarned(address indexed user, VaultType vaultType, uint256 amount, uint256 timestamp);
    event InsurancePolicyPurchased(address indexed user, uint256 coverageAmount, uint256 premiumRate);
    event InsuranceClaimProcessed(address indexed user, uint256 claimAmount, uint256 timestamp);
    event PremiumAccessGranted(address indexed user, string feature, uint256 timestamp);
    event ProtocolFeeUpdated(uint256 oldFee, uint256 newFee);
    event EmergencyWithdrawal(address indexed user, uint256 amount, uint256 fee, uint256 timestamp);
    
    // Modifiers
    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }
    
    modifier validVaultType(VaultType _vaultType) {
        require(_vaultType >= VaultType.MICRO_SAVINGS && _vaultType <= VaultType.EMERGENCY_VAULT, "Invalid vault type");
        _;
    }
    
    modifier validAmount(uint256 _amount) {
        require(_amount > 0, "Amount must be greater than 0");
        _;
    }
    
    modifier hasVault(address _user, VaultType _vaultType) {
        require(userVaults[_user][_vaultType].isActive, "Vault not active");
        _;
    }
    
    // Constructor
    constructor(address _safeToken) {
        owner = msg.sender;
        safeToken = _safeToken;
    }
    
    /**
     * Create a new vault
     */
    function createVault(VaultType _vaultType) external validVaultType(_vaultType) {
        require(!userVaults[msg.sender][_vaultType].isActive, "Vault already exists");
        
        userVaults[msg.sender][_vaultType] = UserVault({
            user: msg.sender,
            vaultType: _vaultType,
            totalDeposited: 0,
            currentBalance: 0,
            totalWithdrawn: 0,
            lastDepositTime: 0,
            lastWithdrawalTime: 0,
            yieldEarned: 0,
            isActive: true,
            autoDepositAmount: 0,
            autoDepositFrequency: 0,
            nextAutoDeposit: 0
        });
        
        totalUsers++;
        
        emit VaultCreated(msg.sender, _vaultType, block.timestamp);
    }
    
    /**
     * Make a deposit to vault
     */
    function depositToVault(VaultType _vaultType, uint256 _amount) external validVaultType(_vaultType) validAmount(_amount) payable {
        require(userVaults[msg.sender][_vaultType].isActive, "Vault not active");
        require(msg.value >= _amount, "Insufficient ETH sent");
        
        UserVault storage vault = userVaults[msg.sender][_vaultType];
        
        // Apply vault-specific logic
        if (_vaultType == VaultType.MICRO_SAVINGS) {
            require(_amount >= 0.001 ether, "Minimum deposit for micro-savings is 0.001 ETH");
        } else if (_vaultType == VaultType.PENSION_NEST) {
            require(_amount >= 0.01 ether, "Minimum deposit for pension nest is 0.01 ETH");
        } else if (_vaultType == VaultType.EMERGENCY_VAULT) {
            require(_amount >= 0.005 ether, "Minimum deposit for emergency vault is 0.005 ETH");
        }
        
        vault.totalDeposited += _amount;
        vault.currentBalance += _amount;
        vault.lastDepositTime = block.timestamp;
        
        totalDeposited += _amount;
        
        emit DepositMade(msg.sender, _vaultType, _amount, block.timestamp);
    }
    
    /**
     * Withdraw from vault
     */
    function withdrawFromVault(VaultType _vaultType, uint256 _amount) external validVaultType(_vaultType) validAmount(_amount) hasVault(msg.sender, _vaultType) {
        UserVault storage vault = userVaults[msg.sender][_vaultType];
        require(vault.currentBalance >= _amount, "Insufficient balance");
        
        uint256 fee = 0;
        
        // Apply vault-specific withdrawal logic
        if (_vaultType == VaultType.EMERGENCY_VAULT) {
            // Emergency vault has instant withdrawal with small fee
            fee = (_amount * emergencyWithdrawalFee) / 10000;
        } else if (_vaultType == VaultType.PENSION_NEST) {
            // Pension nest has time-locked withdrawals
            require(block.timestamp >= vault.lastDepositTime + 365 days, "Pension withdrawals locked for 1 year");
        }
        
        uint256 netAmount = _amount - fee;
        
        vault.currentBalance -= _amount;
        vault.totalWithdrawn += _amount;
        vault.lastWithdrawalTime = block.timestamp;
        
        totalWithdrawn += _amount;
        
        // Transfer funds to user
        (bool success, ) = msg.sender.call{value: netAmount}("");
        require(success, "Transfer failed");
        
        emit WithdrawalMade(msg.sender, _vaultType, _amount, fee, block.timestamp);
    }
    
    /**
     * Enable auto-deposit for vault
     */
    function enableAutoDeposit(
        VaultType _vaultType,
        uint256 _amount,
        uint256 _frequency
    ) external validVaultType(_vaultType) hasVault(msg.sender, _vaultType) {
        require(_amount > 0, "Auto-deposit amount must be positive");
        require(_frequency >= 1, "Frequency must be at least 1 day");
        require(_frequency <= 365, "Frequency cannot exceed 1 year");
        
        UserVault storage vault = userVaults[msg.sender][_vaultType];
        vault.autoDepositAmount = _amount;
        vault.autoDepositFrequency = _frequency;
        vault.nextAutoDeposit = block.timestamp + _frequency * 1 days;
        
        emit AutoDepositEnabled(msg.sender, _vaultType, _amount, _frequency);
    }
    
    /**
     * Execute auto-deposit (called by user or automated system)
     */
    function executeAutoDeposit(VaultType _vaultType) external payable validVaultType(_vaultType) hasVault(msg.sender, _vaultType) {
        UserVault storage vault = userVaults[msg.sender][_vaultType];
        require(vault.autoDepositAmount > 0, "Auto-deposit not enabled");
        require(block.timestamp >= vault.nextAutoDeposit, "Auto-deposit not due yet");
        require(msg.value >= vault.autoDepositAmount, "Insufficient ETH for auto-deposit");
        
        vault.totalDeposited += vault.autoDepositAmount;
        vault.currentBalance += vault.autoDepositAmount;
        vault.lastDepositTime = block.timestamp;
        vault.nextAutoDeposit = block.timestamp + vault.autoDepositFrequency * 1 days;
        
        totalDeposited += vault.autoDepositAmount;
        
        emit AutoDepositExecuted(msg.sender, _vaultType, vault.autoDepositAmount, block.timestamp);
    }
    
    /**
     * Add yield strategy
     */
    function addYieldStrategy(
        string memory _name,
        address _strategyContract,
        uint256 _apy,
        uint256 _riskLevel,
        uint256 _minDeposit,
        uint256 _maxDeposit
    ) external onlyOwner {
        require(_apy > 0, "APY must be positive");
        require(_riskLevel >= 1 && _riskLevel <= 3, "Risk level must be 1-3");
        require(_minDeposit > 0, "Minimum deposit must be positive");
        
        yieldStrategies[nextYieldStrategyId] = YieldStrategy({
            id: nextYieldStrategyId,
            name: _name,
            strategyContract: _strategyContract,
            apy: _apy,
            riskLevel: _riskLevel,
            isActive: true,
            minDeposit: _minDeposit,
            maxDeposit: _maxDeposit,
            totalDeposited: 0
        });
        
        emit YieldStrategyAdded(nextYieldStrategyId, _name, _apy, _riskLevel);
        nextYieldStrategyId++;
    }
    
    /**
     * Calculate and distribute yield
     */
    function calculateYield(address _user, VaultType _vaultType) external validVaultType(_vaultType) hasVault(_user, _vaultType) {
        UserVault storage vault = userVaults[_user][_vaultType];
        require(vault.currentBalance > 0, "No balance to calculate yield");
        
        // Calculate yield based on vault type and strategy
        uint256 baseYield = 0;
        uint256 yieldBoost = 0;
        
        if (_vaultType == VaultType.MICRO_SAVINGS) {
            baseYield = (vault.currentBalance * 300) / 10000; // 3% base APY
            yieldBoost = (vault.currentBalance * microSavingsYieldBoost) / 10000; // 1% boost
        } else if (_vaultType == VaultType.PENSION_NEST) {
            baseYield = (vault.currentBalance * 500) / 10000; // 5% base APY
            yieldBoost = (vault.currentBalance * pensionYieldBoost) / 10000; // 2% boost
        } else if (_vaultType == VaultType.EMERGENCY_VAULT) {
            baseYield = (vault.currentBalance * 200) / 10000; // 2% base APY
            yieldBoost = (vault.currentBalance * emergencyIncentiveRate) / 10000; // 0.25% boost
        }
        
        uint256 totalYield = baseYield + yieldBoost;
        uint256 protocolFee = (totalYield * protocolFeeRate) / 10000;
        uint256 netYield = totalYield - protocolFee;
        
        vault.currentBalance += netYield;
        vault.yieldEarned += netYield;
        totalYieldEarned += netYield;
        
        emit YieldEarned(_user, _vaultType, netYield, block.timestamp);
    }
    
    /**
     * Purchase insurance policy
     */
    function purchaseInsurance(uint256 _coverageAmount) external validAmount(_coverageAmount) {
        require(!insurancePolicies[msg.sender].isActive, "Insurance already active");
        
        insurancePolicies[msg.sender] = InsurancePolicy({
            user: msg.sender,
            coverageAmount: _coverageAmount,
            premiumRate: 10, // 0.1% premium
            coverageStartTime: block.timestamp,
            coverageEndTime: block.timestamp + 365 days,
            isActive: true,
            claimsCount: 0,
            totalClaimsPaid: 0
        });
        
        totalInsurancePolicies++;
        
        emit InsurancePolicyPurchased(msg.sender, _coverageAmount, 10);
    }
    
    /**
     * Process insurance claim
     */
    function processInsuranceClaim(uint256 _claimAmount) external {
        InsurancePolicy storage policy = insurancePolicies[msg.sender];
        require(policy.isActive, "No active insurance policy");
        require(block.timestamp <= policy.coverageEndTime, "Insurance expired");
        require(_claimAmount <= policy.coverageAmount, "Claim exceeds coverage");
        
        policy.claimsCount++;
        policy.totalClaimsPaid += _claimAmount;
        
        // Transfer claim amount to user
        (bool success, ) = msg.sender.call{value: _claimAmount}("");
        require(success, "Transfer failed");
        
        emit InsuranceClaimProcessed(msg.sender, _claimAmount, block.timestamp);
    }
    
    /**
     * Check premium access
     */
    function checkPremiumAccess(address _user) external view returns (bool) {
        return hasPremiumAccess[_user];
    }
    
    /**
     * Grant premium access (called by SAFE token contract)
     */
    function grantPremiumAccess(address _user) external {
        require(msg.sender == safeToken, "Only SAFE token can grant premium access");
        hasPremiumAccess[_user] = true;
        
        emit PremiumAccessGranted(_user, "Premium Vault Access", block.timestamp);
    }
    
    /**
     * Get vault details
     */
    function getVaultDetails(address _user, VaultType _vaultType) external view validVaultType(_vaultType) returns (
        uint256 vaultTotalDeposited,
        uint256 currentBalance,
        uint256 vaultTotalWithdrawn,
        uint256 yieldEarned,
        uint256 lastDepositTime,
        uint256 lastWithdrawalTime,
        bool isActive,
        uint256 autoDepositAmount,
        uint256 autoDepositFrequency
    ) {
        UserVault memory vault = userVaults[_user][_vaultType];
        return (
            vault.totalDeposited,
            vault.currentBalance,
            vault.totalWithdrawn,
            vault.yieldEarned,
            vault.lastDepositTime,
            vault.lastWithdrawalTime,
            vault.isActive,
            vault.autoDepositAmount,
            vault.autoDepositFrequency
        );
    }
    
    /**
     * Get platform statistics
     */
    function getPlatformStats() external view returns (
        uint256 _totalUsers,
        uint256 _totalDeposited,
        uint256 _totalWithdrawn,
        uint256 _totalYieldEarned,
        uint256 _totalInsurancePolicies,
        uint256 _protocolFeeRate
    ) {
        return (
            totalUsers,
            totalDeposited,
            totalWithdrawn,
            totalYieldEarned,
            totalInsurancePolicies,
            protocolFeeRate
        );
    }
    
    /**
     * Admin functions
     */
    function setProtocolFeeRate(uint256 _newRate) external onlyOwner {
        require(_newRate >= 10 && _newRate <= 30, "Fee rate must be between 0.1% and 0.3%");
        uint256 oldRate = protocolFeeRate;
        protocolFeeRate = _newRate;
        emit ProtocolFeeUpdated(oldRate, _newRate);
    }
    
    function setEmergencyWithdrawalFee(uint256 _newFee) external onlyOwner {
        require(_newFee <= 100, "Fee cannot exceed 1%");
        emergencyWithdrawalFee = _newFee;
    }
    
    function setPensionYieldBoost(uint256 _newBoost) external onlyOwner {
        require(_newBoost <= 500, "Boost cannot exceed 5%");
        pensionYieldBoost = _newBoost;
    }
    
    function setMicroSavingsYieldBoost(uint256 _newBoost) external onlyOwner {
        require(_newBoost <= 200, "Boost cannot exceed 2%");
        microSavingsYieldBoost = _newBoost;
    }
    
    function withdrawPlatformFees() external onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No fees to withdraw");
        
        (bool success, ) = owner.call{value: balance}("");
        require(success, "Failed to withdraw fees");
    }
    
    receive() external payable {}
}

contracts/YourContract.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

// Useful for debugging. Remove when deploying to a live network.
import "hardhat/console.sol";

// Use openzeppelin to inherit battle-tested implementations (ERC20, ERC721, etc)
// import "@openzeppelin/contracts/access/Ownable.sol";
// import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * SafeNest - A decentralized help and support system
 * Allows users to request help, provide assistance, and manage community support
 * @author SafeNest Team
 */
contract SafeNest {
    // State Variables
    address public immutable owner;
    string public platformName = "SafeNest - Decentralized Help Platform";
    uint256 public totalHelpRequests = 0;
    uint256 public totalHelpProvided = 0;
    uint256 public platformFee = 0.01 ether; // 0.01 ETH fee for premium help requests
    
    // Help Request Structure
    struct HelpRequest {
        uint256 id;
        address requester;
        string title;
        string description;
        string category;
        uint256 reward;
        bool isResolved;
        bool isPremium;
        uint256 createdAt;
        address helper;
        string solution;
        uint256 resolvedAt;
    }
    
    // User Profile Structure
    struct UserProfile {
        string name;
        string bio;
        uint256 helpRequestsCount;
        uint256 helpProvidedCount;
        uint256 reputation;
        bool isVerified;
        string[] skills;
    }
    
    // Mappings
    mapping(uint256 => HelpRequest) public helpRequests;
    mapping(address => UserProfile) public userProfiles;
    mapping(address => uint256[]) public userHelpRequests;
    mapping(address => uint256[]) public userHelpProvided;
    mapping(string => uint256[]) public categoryRequests;
    mapping(address => bool) public isHelper;
    
    // Events
    event HelpRequestCreated(
        uint256 indexed requestId,
        address indexed requester,
        string title,
        string category,
        uint256 reward,
        bool isPremium
    );
    
    event HelpRequestResolved(
        uint256 indexed requestId,
        address indexed helper,
        string solution
    );
    
    event UserProfileUpdated(address indexed user, string name, string bio);
    event HelperRegistered(address indexed helper, string[] skills);
    event RewardClaimed(uint256 indexed requestId, address indexed helper, uint256 amount);
    
    // Constructor
    constructor() {
        owner = msg.sender;
    }
    
    // Modifiers
    modifier onlyOwner() {
        require(msg.sender == owner, "Not the owner");
        _;
    }
    
    modifier onlyHelper() {
        require(isHelper[msg.sender], "Not a registered helper");
        _;
    }
    
    modifier validRequestId(uint256 _requestId) {
        require(_requestId < totalHelpRequests, "Invalid request ID");
        _;
    }
    
    modifier onlyRequesterOrHelper(uint256 _requestId) {
        HelpRequest storage request = helpRequests[_requestId];
        require(
            msg.sender == request.requester || msg.sender == request.helper,
            "Not authorized"
        );
        _;
    }

    /**
     * Create a new help request
     */
    function createHelpRequest(
        string memory _title,
        string memory _description,
        string memory _category,
        bool _isPremium
    ) public payable returns (uint256) {
        require(bytes(_title).length > 0, "Title cannot be empty");
        require(bytes(_description).length > 0, "Description cannot be empty");
        require(bytes(_category).length > 0, "Category cannot be empty");
        
        if (_isPremium) {
            require(msg.value >= platformFee, "Insufficient fee for premium request");
        }
        
        uint256 requestId = totalHelpRequests;
        uint256 reward = _isPremium ? msg.value - platformFee : 0;
        
        helpRequests[requestId] = HelpRequest({
            id: requestId,
            requester: msg.sender,
            title: _title,
            description: _description,
            category: _category,
            reward: reward,
            isResolved: false,
            isPremium: _isPremium,
            createdAt: block.timestamp,
            helper: address(0),
            solution: "",
            resolvedAt: 0
        });
        
        userHelpRequests[msg.sender].push(requestId);
        categoryRequests[_category].push(requestId);
        totalHelpRequests++;
        
        emit HelpRequestCreated(requestId, msg.sender, _title, _category, reward, _isPremium);
        
        return requestId;
    }
    
    /**
     * Register as a helper
     */
    function registerAsHelper(string memory _name, string memory _bio, string[] memory _skills) public {
        require(bytes(_name).length > 0, "Name cannot be empty");
        require(_skills.length > 0, "Must provide at least one skill");
        
        userProfiles[msg.sender] = UserProfile({
            name: _name,
            bio: _bio,
            helpRequestsCount: 0,
            helpProvidedCount: 0,
            reputation: 0,
            isVerified: false,
            skills: _skills
        });
        
        isHelper[msg.sender] = true;
        
        emit HelperRegistered(msg.sender, _skills);
    }
    
    /**
     * Provide help for a request
     */
    function provideHelp(uint256 _requestId, string memory _solution) 
        public 
        onlyHelper 
        validRequestId(_requestId) 
    {
        HelpRequest storage request = helpRequests[_requestId];
        require(!request.isResolved, "Request already resolved");
        require(request.helper == address(0), "Request already assigned");
        
        request.helper = msg.sender;
        request.solution = _solution;
        request.isResolved = true;
        request.resolvedAt = block.timestamp;
        
        // Update user stats
        userProfiles[msg.sender].helpProvidedCount++;
        userProfiles[request.requester].helpRequestsCount++;
        userProfiles[msg.sender].reputation += 10; // Reputation points for helping
        
        totalHelpProvided++;
        
        // Transfer reward to helper
        if (request.reward > 0) {
            (bool success, ) = msg.sender.call{value: request.reward}("");
            require(success, "Failed to transfer reward");
        }
        
        userHelpProvided[msg.sender].push(_requestId);
        
        emit HelpRequestResolved(_requestId, msg.sender, _solution);
        emit RewardClaimed(_requestId, msg.sender, request.reward);
    }
    
    /**
     * Get help request details
     */
    function getHelpRequest(uint256 _requestId) 
        public 
        view 
        validRequestId(_requestId) 
        returns (HelpRequest memory) 
    {
        return helpRequests[_requestId];
    }
    
    /**
     * Get user profile
     */
    function getUserProfile(address _user) public view returns (UserProfile memory) {
        return userProfiles[_user];
    }
    
    /**
     * Get help requests by category
     */
    function getHelpRequestsByCategory(string memory _category) 
        public 
        view 
        returns (uint256[] memory) 
    {
        return categoryRequests[_category];
    }
    
    /**
     * Get user's help requests
     */
    function getUserHelpRequests(address _user) public view returns (uint256[] memory) {
        return userHelpRequests[_user];
    }
    
    /**
     * Get user's help provided
     */
    function getUserHelpProvided(address _user) public view returns (uint256[] memory) {
        return userHelpProvided[_user];
    }
    
    /**
     * Update user profile
     */
    function updateProfile(string memory _name, string memory _bio) public {
        require(bytes(_name).length > 0, "Name cannot be empty");
        
        userProfiles[msg.sender].name = _name;
        userProfiles[msg.sender].bio = _bio;
        
        emit UserProfileUpdated(msg.sender, _name, _bio);
    }
    
    /**
     * Verify a helper (only owner)
     */
    function verifyHelper(address _helper) public onlyOwner {
        require(isHelper[_helper], "User is not a helper");
        userProfiles[_helper].isVerified = true;
    }
    
    /**
     * Set platform fee (only owner)
     */
    function setPlatformFee(uint256 _newFee) public onlyOwner {
        platformFee = _newFee;
    }
    
    /**
     * Withdraw platform fees (only owner)
     */
    function withdrawFees() public onlyOwner {
        uint256 balance = address(this).balance;
        require(balance > 0, "No fees to withdraw");
        
        (bool success, ) = owner.call{value: balance}("");
        require(success, "Failed to withdraw fees");
    }
    
    /**
     * Get platform statistics
     */
    function getPlatformStats() public view returns (
        uint256 _totalRequests,
        uint256 _totalHelpProvided,
        uint256 _totalHelpers,
        uint256 _platformBalance
    ) {
        return (
            totalHelpRequests,
            totalHelpProvided,
            // Note: This is a simplified count - in production, you'd want to track this properly
            totalHelpRequests, // Placeholder for total helpers
            address(this).balance
        );
    }

    /**
     * Function that allows the contract to receive ETH
     */
    receive() external payable {}
}

